            remaining = this.safeString(order, "amount");
        } else
        {
            remaining = this.safeString(order, "amount");
        }
        object fee = null;
        return this.safeOrder(new Dictionary<string, object>() {
            { "info", order },
            { "id", id },
            { "clientOrderId", null },
