        //        "postOnly": false,
        //        "reduceOnly": false,
        //        "executedSize": "0",
        //        "status": "booked",
        //        "avgPrice": "0",
        //        "executedNotional": "0",
        //        "creditFeePaid": "0",
        //        "marginBonusFeePaid": "0",
        //        "quoteFeePaid": "0",
        //        "arkmFeePaid": "0",
        //        "revisionId": "887956326",
