        //            "price": "180.322010164"
        //        }
        //
        object address = this.safeString(transaction, "depositAddress");
        object timestamp = this.safeIntegerProduct(transaction, "time", 0.001);
        object confirmd = this.safeBool(transaction, "confirmed");
        object status = null;
        if (isTrue(confirmd))
        {
