            { "network", this.networkIdToCode(this.safeString(transaction, "chain")) },
            { "amount", this.safeNumber(transaction, "amount") },
            { "status", status },
            { "timestamp", timestamp },
            { "datetime", this.iso8601(timestamp) },
            { "address", address },
            { "addressFrom", null },
            { "addressTo", address },
            { "tag", null },
            { "tagFrom", null },
            { "tagTo", null },
            { "updated", null },
            { "comment", null },
            { "fee", null },
            { "internal", false },
