                { "swap", swap },
                { "future", future },
                { "option", false },
                { "active", true },
                { "contract", contract },
                { "linear", ((bool) isTrue(spot)) ? null : true },
                { "inverse", ((bool) isTrue(spot)) ? null : false },
                { "taker", taker },
                { "maker", maker },
                { "contractSize", null },
                { "expiry", expiry },
                { "expiryDatetime", this.iso8601(expiry) },
                { "strike", null },
