            }
            object sinceDate = this.iso8601(since);
            object untilDate = this.iso8601(until);
            ((IDictionary<string,object>)parameters)[(string)sinceKey] = sinceDate;
            ((IDictionary<string,object>)parameters)[(string)untilKey] = untilDate;
        }
        return parameters;
    }

