            { "baseVolume", this.safeString(ticker, "target_volume") },
            { "quoteVolume", this.safeString(ticker, "quote_volume") },
            { "info", ticker },
        }, market);
    }

    public override object parseTrade(object trade, object market = null)
    {
        //
        // fetchTrades (public)
        //
        //     {
        //         "id": "1701075265708001",
        //         "timestamp": 1701075265708,
        //         "price": "50020000",
        //         "qty": "0.00155177",
        //         "is_seller_maker": false
        //     }
        //
        // fetchMyTrades (private)
        //
        //     {
        //         "timestamp": "1416561032",
        //         "price": "419000.0",
        //         "type": "bid",
        //         "qty": "0.001",
        //         "feeRate": "-0.0015",
        //         "fee": "-0.0000015",
        //         "orderId": "E84A1AC2-8088-4FA0-B093-A3BCDB9B3C85"
        //     }
        //
        object timestamp = this.safeInteger(trade, "timestamp");
        market = this.safeMarket(null, market);
        object isSellerMaker = this.safeBool(trade, "is_seller_maker");
        object side = null;
        if (isTrue(!isEqual(isSellerMaker, null)))
        {
            side = ((bool) isTrue(isSellerMaker)) ? "sell" : "buy";
        }
        object priceString = this.safeString(trade, "price");
        object amountString = this.safeString(trade, "qty");
        object orderId = this.safeString(trade, "orderId");
        object feeCostString = this.safeString(trade, "fee");
        object fee = null;
        if (isTrue(!isEqual(feeCostString, null)))
        {
            feeCostString = Precise.stringAbs(feeCostString);
            object feeRateString = this.safeString(trade, "feeRate");
            feeRateString = Precise.stringAbs(feeRateString);
            object feeCurrencyCode = ((bool) isTrue((isEqual(side, "sell")))) ? getValue(market, "quote") : getValue(market, "base");
            fee = new Dictionary<string, object>() {
                { "cost", feeCostString },
                { "currency", feeCurrencyCode },
                { "rate", feeRateString },
            };
        }
        return this.safeTrade(new Dictionary<string, object>() {
            { "id", this.safeString(trade, "id") },
            { "info", trade },
            { "timestamp", timestamp },
            { "datetime", this.iso8601(timestamp) },
            { "order", orderId },
            { "symbol", getValue(market, "symbol") },
            { "type", null },
            { "side", side },
            { "takerOrMaker", null },
            { "price", priceString },
            { "amount", amountString },
            { "cost", null },
            { "fee", fee },
        }, market);
    }

