            ((IDictionary<string,object>)account)["total"] = this.safeString(balance, "quantity");
            ((IDictionary<string,object>)account)["used"] = this.safeString(balance, "reserved_qty");
            ((IDictionary<string,object>)result)[(string)code] = account;
