        }
        if (isTrue((obj is string)))
        {
            return obj;
        }
        object returnString = "";
        object paramsKeys = null;
        if (isTrue(((obj is IList<object>) || (obj.GetType().IsGenericType && obj.GetType().GetGenericTypeDefinition().IsAssignableFrom(typeof(List<>))))))
        {
            paramsKeys = obj;
        } else
        {
