    public override object parseOrder(object order, object market = null)
    {
        //
        // createOrder
        //     {
        //         "order_id": "01JEXAFCCC5ZVJPZAAHHDKQBNG"
        //     }
        //
        // fetchOrders
        //     {
        //         "id": "01JEXAPY04JDFBVFC2D23BCKMK",
        //         "type": "market",
        //         "direction": "sell",
        //         "symbol": "TRX_USDT",
        //         "quantity": "67.5400000000000000",
        //         "filledQuantity": "67.5400000000000000",
        //         "filledValue": "20.0053480000000000",
        //         "state": "completed",
        //         "internalState": "filled",
        //         "createdAt": "2024-12-12 11:40:19",
        //         "finishedAt": "2024-12-12 11:40:21",
        //         "deal": {
        //             "id": "01JEXAPZ9C9TWENPFZJASZ1YD2",
        //             "state": "completed",
        //             "createdAt": "2024-12-12 11:40:21",
        //             "completedAt": "2024-12-12 11:40:21",
        //             "averageFilledPrice": "0.2962000000000000",
        //             "transactions": [
        //                 {
        //                     "id": "01JEXAPZ9C9TWENPFZJASZ1YD3",
        //                     "tradeRole": "taker",
        //                     "filledPrice": "0.2962000000000000",
        //                     "filledQuantity": "67.5400000000000000",
        //                     "filledValue": "20.0053480000000000",
        //                     "fee": "0.0000000000000000",
        //                     "feeCurrency": "USDT",
        //                     "committedAt": "2024-12-12 11:40:21"
        //                 }
        //             ]
        //         }
        //     },
        //     ...
        //
        // fetchOpenOrders
        //     {
        //         "id": "01JFFG72CBRDP68K179KC9DSTG",
        //         "direction": "sell",
        //         "symbol": "BTC_USDT",
        //         "price": "102.0130000000000000",
        //         "quantity": "0.0005000000000000",
        //         "value": "0.0510065000000000",
        //         "filledQuantity": "0.0000000000000000",
        //         "filledValue": "0.0000000000000000",
        //         "createdAt": "2024-12-19 09:02:51",
        //         "clientOrderId": "987654321",
        //         "stopLossPrice": "101.12"
        //     }
        //
        object id = this.safeString2(order, "order_id", "id");
        object marketId = this.safeString(order, "symbol");
        market = this.safeMarket(marketId, market);
        object dateTime = this.safeString(order, "createdAt");
        object timestamp = this.parse8601(dateTime);
        object deal = this.safeDict(order, "deal", new Dictionary<string, object>() {});
        object averageFilledPrice = this.safeNumber(deal, "averageFilledPrice");
        object type = this.safeString(order, "type");
        object side = this.safeString(order, "direction");
        object price = this.safeNumber(order, "price");
        object transaction = this.safeList(deal, "transactions", new List<object>() {});
        object fee = null;
        object firstTx = this.safeDict(transaction, 0);
        object feeCurrency = this.safeString(firstTx, "feeCurrency");
        if (isTrue(!isEqual(feeCurrency, null)))
        {
            fee = new Dictionary<string, object>() {
                { "currency", this.safeCurrencyCode(feeCurrency) },
                { "cost", this.safeNumber(firstTx, "fee") },
            };
        }
        if (isTrue(isEqual(price, null)))
        {
            price = this.safeNumber(firstTx, "filledPrice");
        }
        object amount = this.safeNumber(order, "quantity");
        object cost = this.safeNumber(order, "value");
        object status = this.parseOrderStatus(this.safeString(order, "state"));
        object clientOrderId = this.safeString(order, "clientOrderId");
        return this.safeOrder(new Dictionary<string, object>() {
            { "id", id },
            { "clientOrderId", clientOrderId },
            { "timestamp", timestamp },
            { "datetime", this.iso8601(timestamp) },
            { "lastTradeTimestamp", null },
            { "symbol", getValue(market, "symbol") },
            { "type", type },
            { "timeInForce", null },
            { "postOnly", null },
            { "side", side },
            { "price", price },
            { "stopPrice", this.safeString(order, "stopLossPrice") },
            { "triggerPrice", this.safeString(order, "stopLossPrice") },
            { "amount", amount },
            { "cost", cost },
            { "average", averageFilledPrice },
            { "filled", this.safeString(order, "filledQuantity") },
            { "remaining", null },
            { "status", status },
            { "fee", fee },
            { "trades", null },
            { "info", order },
        }, market);
    }

    public virtual object parseOrderStatus(object status = null)
    {
        object statuses = new Dictionary<string, object>() {
            { "active", "open" },
            { "completed", "closed" },
            { "partially_completed", "open" },
            { "cancelled", "canceled" },
            { "expired", "expired" },
            { "failed", "failed" },
        };
        return this.safeString(statuses, status, status);
    }

    /**
     * @method
     * @name cryptomus#fetchTradingFees
     * @description fetch the trading fees for multiple markets
     * @see https://trade-docs.coinlist.co/?javascript--nodejs#list-fees
     * @param {object} [params] extra parameters specific to the exchange API endpoint
     * @returns {object} a dictionary of [fee structures]{@link https://docs.ccxt.com/?id=fee-structure} indexed by market symbols
     */
    public async override Task<object> fetchTradingFees(object parameters = null)
    {
        parameters ??= new Dictionary<string, object>();
        object response = await this.privateGetV2UserApiExchangeAccountTariffs(parameters);
        //
        //     {
        //         result: {
        //             equivalent_currency_code: 'USD',
        //             current_tariff_step: {
        //                 step: '0',
        //                 from_turnover: '0.00000000',
        //                 maker_percent: '0.08',
        //                 taker_percent: '0.1'
        //             },
        //             tariff_steps: [
        //                 {
        //                     step: '0',
        //                     from_turnover: '0.00000000',
        //                     maker_percent: '0.08',
        //                     taker_percent: '0.1'
        //                 },
        //                 {
        //                     step: '1',
        //                     from_turnover: '100001.00000000',
        //                     maker_percent: '0.06',
        //                     taker_percent: '0.095'
        //                 },
        //                 {
        //                     step: '2',
        //                     from_turnover: '250001.00000000',
        //                     maker_percent: '0.055',
        //                     taker_percent: '0.085'
        //                 },
        //                 {
        //                     step: '3',
        //                     from_turnover: '500001.00000000',
        //                     maker_percent: '0.05',
        //                     taker_percent: '0.075'
        //                 },
        //                 {
        //                     step: '4',
        //                     from_turnover: '2500001.00000000',
        //                     maker_percent: '0.04',
        //                     taker_percent: '0.07'
        //                 }
        //             ],
        //             daily_turnover: '0.00000000',
        //             monthly_turnover: '77.52062617',
        //             circulation_funds: '25.48900443'
        //         }
        //     }
        //
        object data = this.safeDict(response, "result", new Dictionary<string, object>() {});
        object currentFeeTier = this.safeDict(data, "current_tariff_step", new Dictionary<string, object>() {});
        object makerFee = this.safeString(currentFeeTier, "maker_percent");
        object takerFee = this.safeString(currentFeeTier, "taker_percent");
        makerFee = Precise.stringDiv(makerFee, "100");
        takerFee = Precise.stringDiv(takerFee, "100");
        object feeTiers = this.safeList(data, "tariff_steps", new List<object>() {});
        object result = new Dictionary<string, object>() {};
        object tiers = this.parseFeeTiers(feeTiers);
        for (object i = 0; isLessThan(i, getArrayLength(this.symbols)); postFixIncrement(ref i))
        {
            object symbol = getValue(this.symbols, i);
            ((IDictionary<string,object>)result)[(string)symbol] = new Dictionary<string, object>() {
                { "info", response },
                { "symbol", symbol },
                { "maker", this.parseNumber(makerFee) },
                { "taker", this.parseNumber(takerFee) },
                { "percentage", true },
                { "tierBased", true },
                { "tiers", tiers },
            };
        }
        return result;
    }

    public virtual object parseFeeTiers(object feeTiers, object market = null)
    {
        object takerFees = new List<object>() {};
        object makerFees = new List<object>() {};
        for (object i = 0; isLessThan(i, getArrayLength(feeTiers)); postFixIncrement(ref i))
        {
            object tier = getValue(feeTiers, i);
            object turnover = this.safeNumber(tier, "from_turnover");
            object taker = this.safeString(tier, "taker_percent");
            object maker = this.safeString(tier, "maker_percent");
            maker = Precise.stringDiv(maker, "100");
            taker = Precise.stringDiv(taker, "100");
            ((IList<object>)makerFees).Add(new List<object>() {turnover, this.parseNumber(maker)});
            ((IList<object>)takerFees).Add(new List<object>() {turnover, this.parseNumber(taker)});
        }
        return new Dictionary<string, object>() {
            { "maker", makerFees },
            { "taker", takerFees },
        };
    }

    public override object sign(object path, object api = null, object method = null, object parameters = null, object headers = null, object body = null)
    {
        api ??= "public";
        method ??= "GET";
        parameters ??= new Dictionary<string, object>();
        object endpoint = this.implodeParams(path, parameters);
        parameters = this.omit(parameters, this.extractParams(path));
        object url = add(add(getValue(getValue(this.urls, "api"), api), "/"), endpoint);
        if (isTrue(isEqual(api, "private")))
        {
            this.checkRequiredCredentials();
            object jsonParams = "";
            headers = new Dictionary<string, object>() {
                { "userId", this.uid },
            };
            if (isTrue(!isEqual(method, "GET")))
            {
                body = this.json(parameters);
                jsonParams = body;
                ((IDictionary<string,object>)headers)["Content-Type"] = "application/json";
            } else
            {
                object query = this.urlencode(parameters);
                if (isTrue(!isEqual(getArrayLength(query), 0)))
                {
                    url = add(url, add("?", query));
                }
            }
            object jsonParamsBase64 = this.stringToBase64(jsonParams);
            object stringToSign = add(jsonParamsBase64, this.secret);
            object signature = this.hash(this.encode(stringToSign), md5);
            ((IDictionary<string,object>)headers)["sign"] = signature;
        } else
        {
            object query = this.urlencode(parameters);
            if (isTrue(!isEqual(getArrayLength(query), 0)))
            {
                url = add(url, add("?", query));
            }
        }
        return new Dictionary<string, object>() {
            { "url", url },
            { "method", method },
            { "body", body },
            { "headers", headers },
        };
    }

    public override object handleErrors(object httpCode, object reason, object url, object method, object headers, object body, object response, object requestHeaders, object requestBody)
    {
        if (isTrue(isEqual(response, null)))
        {
            return null;
        }
        if (isTrue(inOp(response, "code")))
        {
            object code = this.safeString(response, "code");
            object feedback = add(add(this.id, " "), body);
            this.throwExactlyMatchedException(getValue(this.exceptions, "exact"), code, feedback);
            throw new ExchangeError ((string)feedback) ;
        } else if (isTrue(inOp(response, "message")))
        {
            //
            //      {"message":"Minimum amount 15 USDT","state":1}
            //
            object message = this.safeString(response, "message");
            object feedback = add(add(this.id, " "), body);
            this.throwExactlyMatchedException(getValue(this.exceptions, "exact"), message, feedback);
            this.throwBroadlyMatchedException(getValue(this.exceptions, "broad"), message, feedback);
            throw new ExchangeError ((string)feedback) ;
        }
        return null;
    }
