            { "vwap", null },
            { "open", this.safeString(ticker, "open") },
            { "close", last },
            { "last", last },
            { "previousClose", null },
            { "change", null },
            { "percentage", this.safeString(ticker, "change") },
            { "average", null },
            { "baseVolume", this.safeString2(ticker, "vol", "volume") },
            { "quoteVolume", this.safeString(ticker, "deal") },
            { "info", ticker },
        }, market);
    }

