    /**
     * @method
     * @name bydfi#watchOrdersForSymbols
     * @description watches information on multiple orders made by the user
     * @see https://developers.bydfi.com/en/swap/websocket-account#order-trade-update-push
     * @param {string[]} symbols unified symbol of the market to fetch orders for
     * @param {int} [since] the earliest time in ms to fetch orders for
     * @param {int} [limit] the maximum number of trade structures to retrieve
     * @param {object} [params] extra parameters specific to the exchange API endpoint
     * @returns {object[]} a list of [order structures]{@link https://docs.ccxt.com/?id=order-structure}
     */
    public async override Task<object> watchOrdersForSymbols(object symbols, object since = null, object limit = null, object parameters = null)
    {
        parameters ??= new Dictionary<string, object>();
        await this.loadMarkets();
        symbols = this.marketSymbols(symbols, null, true);
        object messageHashes = new List<object>() {};
        if (isTrue(isEqual(symbols, null)))
        {
            ((IList<object>)messageHashes).Add("orders");
        } else
        {
            for (object i = 0; isLessThan(i, getArrayLength(symbols)); postFixIncrement(ref i))
            {
                object symbol = getValue(symbols, i);
                ((IList<object>)messageHashes).Add(add("orders::", symbol));
            }
        }
        object orders = await this.watchPrivate(messageHashes, parameters);
        if (isTrue(this.newUpdates))
        {
            object first = this.safeValue(orders, 0);
            object tradeSymbol = this.safeString(first, "symbol");
            limit = callDynamically(orders, "getLimit", new object[] {tradeSymbol, limit});
        }
        return this.filterBySinceLimit(orders, since, limit, "timestamp", true);
    }

    public virtual void handleOrder(WebSocketClient client, object message)
    {
        //
        //     {
        //         "T": 1766588450558,
        //         "E": 1766588450685,
        //         "e": "ORDER_TRADE_UPDATE",
        //         "o": {
        //             "S": "BUY",
        //             "ap": "0",
        //             "cpt": false,
        //             "ct": "future",
        //             "ev": "0",
        //             "fee": "0",
        //             "lv": 2,
        //             "mt": "isolated",
        //             "o": "7409609004526010368",
        //             "p": "1000",
        //             "ps": "BOTH",
        //             "pt": "ONE_WAY",
        //             "ro": false,
        //             "s": "ETH-USDC",
        //             "st": "NEW",
        //             "t": "LIMIT",
        //             "tp": "0",
        //             "u": "0.001",
        //             "v": "2"
        //         }
        //     }
        //
        object rawOrder = this.safeDict(message, "o", new Dictionary<string, object>() {});
        object marketId = this.safeString(rawOrder, "s");
        object market = this.safeMarket(marketId);
        object symbol = getValue(market, "symbol");
        object match = false;
        object messageHash = "orders";
        object symbolMessageHash = add(add(messageHash, "::"), symbol);
        object messageHashes = this.findMessageHashes(client as WebSocketClient, messageHash);
        for (object i = 0; isLessThan(i, getArrayLength(messageHashes)); postFixIncrement(ref i))
        {
            object hash = getValue(messageHashes, i);
            if (isTrue(isTrue(isEqual(hash, symbolMessageHash)) || isTrue(isEqual(hash, messageHash))))
            {
                match = true;
                break;
            }
        }
        if (isTrue(match))
        {
            if (isTrue(isEqual(this.orders, null)))
            {
                object limit = this.safeInteger(this.options, "ordersLimit", 1000);
                this.orders = new ArrayCacheBySymbolById(limit);
            }
            object orders = this.orders;
            object order = this.parseWsOrder(rawOrder, market);
            object lastUpdateTimestamp = this.safeInteger(message, "T");
            ((IDictionary<string,object>)order)["lastUpdateTimestamp"] = lastUpdateTimestamp;
            callDynamically(orders, "append", new object[] {order});
            callDynamically(client as WebSocketClient, "resolve", new object[] {orders, messageHash});
            callDynamically(client as WebSocketClient, "resolve", new object[] {orders, symbolMessageHash});
        }
    }

    public override object parseWsOrder(object order, object market = null)
    {
        //
        //     {
        //         "S": "BUY",
        //         "ap": "0",
        //         "cpt": false,
        //         "ct": "future",
        //         "ev": "0",
        //         "fee": "0",
        //         "lv": 2,
        //         "mt": "isolated",
        //         "o": "7409609004526010368",
        //         "p": "1000",
        //         "ps": "BOTH",
        //         "pt": "ONE_WAY",
        //         "ro": false,
        //         "s": "ETH-USDC",
        //         "st": "NEW",
        //         "t": "LIMIT",
        //         "tp": "0",
        //         "u": "0.001",
        //         "v": "2"
        //     }
        //
        object marketId = this.safeString(order, "s");
        market = this.safeMarket(marketId, market);
        object rawStatus = this.safeString(order, "st");
        object rawType = this.safeString(order, "t");
        object fee = null;
        object feeCost = this.safeString(order, "fee");
        if (isTrue(!isEqual(feeCost, null)))
        {
            fee = new Dictionary<string, object>() {
                { "cost", Precise.stringAbs(feeCost) },
                { "currency", getValue(market, "quote") },
            };
        }
        return this.safeOrder(new Dictionary<string, object>() {
            { "info", order },
            { "id", this.safeString(order, "o") },
            { "clientOrderId", this.safeString(order, "cid") },
            { "timestamp", null },
            { "datetime", null },
            { "lastTradeTimestamp", null },
            { "lastUpdateTimestamp", null },
            { "status", this.parseOrderStatus(rawStatus) },
            { "symbol", getValue(market, "symbol") },
            { "type", this.parseOrderType(rawType) },
            { "timeInForce", null },
            { "postOnly", null },
            { "reduceOnly", this.safeBool(order, "ro") },
            { "side", this.safeStringLower(order, "S") },
            { "price", this.safeString(order, "p") },
            { "triggerPrice", null },
            { "stopLossPrice", null },
            { "takeProfitPrice", null },
            { "amount", this.safeString(order, "v") },
            { "filled", this.safeString(order, "ev") },
            { "remaining", this.safeString(order, "qty") },
            { "cost", null },
            { "trades", null },
            { "fee", fee },
            { "average", this.omitZero(this.safeString(order, "ap")) },
        }, market);
    }

    /**
     * @method
     * @name bydfi#watchPositions
     * @description watch all open positions
     * @see https://developers.bydfi.com/en/swap/websocket-account#balance-and-position-update-push
     * @param {string[]} [symbols] list of unified market symbols
     * @param {int} [since] the earliest time in ms to fetch positions for
     * @param {int} [limit] the maximum number of positions to retrieve
     * @param {object} params extra parameters specific to the exchange API endpoint
     * @returns {object[]} a list of [position structure]{@link https://docs.ccxt.com/en/latest/manual.html#position-structure}
     */
    public async override Task<object> watchPositions(object symbols = null, object since = null, object limit = null, object parameters = null)
    {
        parameters ??= new Dictionary<string, object>();
        await this.loadMarkets();
        symbols = this.marketSymbols(symbols, null, true);
        object messageHashes = new List<object>() {};
        object messageHash = "positions";
        if (isTrue(isEqual(symbols, null)))
        {
            ((IList<object>)messageHashes).Add(messageHash);
        } else
        {
            for (object i = 0; isLessThan(i, getArrayLength(symbols)); postFixIncrement(ref i))
            {
