            { "stopPrice", null },
            { "triggerPrice", null },
            { "amount", this.safeString(order, "q") },
            { "cost", null },
            { "average", this.safeString(order, "p") },
            { "filled", this.safeString(order, "z") },
            { "remaining", null },
            { "status", this.parseOrderStatus(this.safeString(order, "X")) },
            { "fee", fee },
            { "trades", null },
        }, market);
    }

