        //             "transactionFee": "0.004",
        //             "failureInfo":"The address is not valid. Please confirm with the recipient",
        //             "txId": "0xb5ef8c13b968a406cc62a93a8bd80f9e9a906ef1b3fcf20a2e48573c17659268"
        //         }
        //     ]
        //
        return this.parseTransactions(response, currency, since, limit);
    }

    public virtual object parseTransactionStatus(object status)
    {
        object statuses = new Dictionary<string, object>() {
            { "0", "ok" },
            { "1", "fail" },
            { "2", "pending" },
            { "3", "canceled" },
        };
        return this.safeString(statuses, status, status);
    }

    public override object parseTransaction(object transaction, object currency = null)
    {
        //
        //     {
        //         "address": "0x94df8b352de7f46f64b01d3666bf6e936e44ce60",
        //         "amount": "8.91000000",
        //         "createdAt": "2019-10-12 09:12:02",
        //         "lastUpdated": "2019-10-12 11:12:02",
        //         "coin": "USDT",
        //         "id": "b6ae22b3aa844210a7041aee7589627c",
        //         "withdrawOrderId": "WITHDRAWtest123",
        //         "network": "ETH",
        //         "status": 1,
        //         "transactionFee": "0.004",
        //         "failureInfo": "The address is not valid. Please confirm with the recipient",
        //         "txId": "0xb5ef8c13b968a406cc62a93a8bd80f9e9a906ef1b3fcf20a2e48573c17659268"
        //     }
        //
        object currencyId = this.safeString(transaction, "coin");
        object code = this.safeCurrencyCode(currencyId, currency);
        object timestamp = this.parse8601(this.safeString(transaction, "createdAt"));
        object updated = this.parse8601(this.safeString(transaction, "lastUpdated"));
        object status = this.parseTransactionStatus(this.safeString(transaction, "status"));
        object feeCost = this.safeNumber(transaction, "transactionFee");
        object fee = null;
        if (isTrue(!isEqual(feeCost, null)))
        {
            fee = new Dictionary<string, object>() {
                { "cost", feeCost },
                { "currency", code },
            };
        }
        return new Dictionary<string, object>() {
            { "info", transaction },
            { "id", this.safeString(transaction, "id") },
            { "txid", this.safeString(transaction, "txId") },
            { "timestamp", timestamp },
            { "datetime", this.iso8601(timestamp) },
            { "network", this.networkIdToCode(this.safeString(transaction, "network")) },
            { "address", this.safeString(transaction, "address") },
            { "addressTo", this.safeString(transaction, "address") },
            { "addressFrom", null },
            { "tag", null },
            { "tagTo", null },
            { "tagFrom", null },
            { "type", "withdrawal" },
            { "amount", this.safeNumber(transaction, "amount") },
            { "currency", code },
            { "status", status },
            { "updated", updated },
            { "fee", fee },
            { "internal", null },
            { "comment", null },
        };
    }

    public override object sign(object path, object api = null, object method = null, object parameters = null, object headers = null, object body = null)
    {
        api ??= "public";
        method ??= "GET";
        parameters ??= new Dictionary<string, object>();
        object url = add(add(getValue(getValue(this.urls, "api"), "rest"), "/"), path);
        if (isTrue(isEqual(api, "public")))
        {
            if (isTrue(getArrayLength(new List<object>(((IDictionary<string,object>)parameters).Keys))))
            {
                url = add(url, add("?", this.urlencode(parameters)));
            }
        }
        if (isTrue(isEqual(api, "private")))
        {
            this.checkRequiredCredentials();
            object timestamp = this.milliseconds();
            object data = this.extend(new Dictionary<string, object>() {
                { "recvWindow", getValue(this.options, "recvWindow") },
                { "timestamp", timestamp },
            }, parameters);
            data = this.keysort(data);
            object signature = this.hmac(this.encode(this.urlencode(data)), this.encode(this.secret), sha256);
            url = add(url, add("?", this.urlencode(data)));
            url = add(url, add(add("&", "signature="), signature));
            headers = new Dictionary<string, object>() {
                { "Content-Type", "application/x-www-form-urlencoded" },
                { "X-Api-Key", this.apiKey },
            };
        }
        return new Dictionary<string, object>() {
            { "url", url },
            { "method", method },
            { "body", body },
            { "headers", headers },
        };
    }

    public override object handleErrors(object code, object reason, object url, object method, object headers, object body, object response, object requestHeaders, object requestBody)
    {
        //
        // {"code":2098,"message":"Request out of receiving window."}
        //
        if (isTrue(isEqual(response, null)))
        {
            return null;
        }
        object errorCode = this.safeString(response, "code");
        if (isTrue(!isEqual(errorCode, null)))
        {
            object feedback = add(add(this.id, " "), body);
            this.throwExactlyMatchedException(getValue(this.exceptions, "exact"), errorCode, feedback);
            throw new ExchangeError ((string)feedback) ;
        }
        return null;
    }
}
