public partial class BaseTest
{
        public void testCapitalize()
        {
            var exchange = new ccxt.Exchange(new Dictionary<string, object>() {
                { "id", "sampleexchange" },
            });
            // Test 1: Basic lowercase string
            Assert(isEqual(exchange.capitalize("hello"), "Hello"));
            // Test 2: Preserve rest of string (camelCase)
            Assert(isEqual(exchange.capitalize("fooBar"), "FooBar"));
            // Test 3: Preserve rest of string (mixed case)
            Assert(isEqual(exchange.capitalize("helloWorld"), "HelloWorld"));
            // Test 4: Already capitalized
            Assert(isEqual(exchange.capitalize("Hello"), "Hello"));
            // Test 5: All uppercase (should preserve rest)
            Assert(isEqual(exchange.capitalize("hELLO"), "HELLO"));
            // Test 6: Single character lowercase
            Assert(isEqual(exchange.capitalize("a"), "A"));
            // Test 7: Single character uppercase
            Assert(isEqual(exchange.capitalize("A"), "A"));
            // Test 8: Empty string
            Assert(isEqual(exchange.capitalize(""), ""));
            // Test 9: String starting with number
            Assert(isEqual(exchange.capitalize("123abc"), "123abc"));
            // Test 10: String with spaces
            Assert(isEqual(exchange.capitalize("hello world"), "Hello world"));
            // Test 11: Underscore separated (snake_case)
            Assert(isEqual(exchange.capitalize("foo_bar_baz"), "Foo_bar_baz"));
            // Test 12: All caps input (preserve rest)
            Assert(isEqual(exchange.capitalize("aBC"), "ABC"));
        }
}