using ccxt;
using ccxt.pro;
namespace Tests;

// PLEASE DO NOT EDIT THIS FILE, IT IS GENERATED AND WILL BE OVERWRITTEN:
// https://github.com/ccxt/ccxt/blob/master/CONTRIBUTING.md#how-to-contribute-code

public partial class BaseTest
{
        public void testPrecisionFromString()
        {
            var exchange = new ccxt.Exchange(new Dictionary<string, object>() {
                { "id", "sampleexchange" },
            });
            // Test 1: Scientific notation negative exponent
            Assert(isEqual(exchange.precisionFromString("1e-4"), 4));
            // Test 2: Scientific notation uppercase E
            Assert(isEqual(exchange.precisionFromString("1E-4"), 4));
            // Test 3: Scientific notation larger negative exponent
            Assert(isEqual(exchange.precisionFromString("1e-8"), 8));
            // Test 4: Scientific notation with decimal
            Assert(isEqual(exchange.precisionFromString("2.5e-6"), 6));
            // Test 5: Scientific notation positive exponent
            Assert(isEqual(exchange.precisionFromString("1e4"), -4));
            // Test 6: Scientific notation explicit positive
            Assert(isEqual(exchange.precisionFromString("1e+4"), -4));
            // Test 7: Decimal string 4 places
            Assert(isEqual(exchange.precisionFromString("0.0001"), 4));
            // Test 8: Decimal string 5 places
            Assert(isEqual(exchange.precisionFromString("0.00001"), 5));
            // Test 9: Decimal string 1 place
            Assert(isEqual(exchange.precisionFromString("0.1"), 1));
            // Test 10: Decimal string 2 places
            Assert(isEqual(exchange.precisionFromString("0.01"), 2));
            // Test 11: Decimal string 8 places
            Assert(isEqual(exchange.precisionFromString("0.00000001"), 8));
            // Test 12: Trailing zeros should be stripped
            Assert(isEqual(exchange.precisionFromString("0.0100"), 2));
            // Test 13: Trailing zeros stripped
            Assert(isEqual(exchange.precisionFromString("0.00100"), 3));
            // Test 14: All trailing zeros after decimal
            Assert(isEqual(exchange.precisionFromString("1.0000"), 0));
            // Test 15: Integer string (no decimal)
            Assert(isEqual(exchange.precisionFromString("1"), 0));
            // Test 16: Integer string larger
            Assert(isEqual(exchange.precisionFromString("10"), 0));
            // Test 17: Integer string even larger
            Assert(isEqual(exchange.precisionFromString("100"), 0));
            // Test 18: Zero with decimal
            Assert(isEqual(exchange.precisionFromString("0.0"), 0));
            // Test 19: One with decimal
            Assert(isEqual(exchange.precisionFromString("1.0"), 0));
            // Test 20: Mixed precision
            Assert(isEqual(exchange.precisionFromString("0.12345"), 5));
        }
}