public partial class BaseTest
{
        public void testUrlencodeBase64()
        {
            var exchange = new ccxt.Exchange(new Dictionary<string, object>() {
                { "id", "sampleexchange" },
            });
            // Test 1: Simple string
            Assert(isEqual(exchange.urlencodeBase64("hello"), "aGVsbG8"));
            // Test 2: String with space
            Assert(isEqual(exchange.urlencodeBase64("hello world"), "aGVsbG8gd29ybGQ"));
            // Test 3: Short string
            Assert(isEqual(exchange.urlencodeBase64("test"), "dGVzdA"));
            // Test 4: Empty string
            Assert(isEqual(exchange.urlencodeBase64(""), ""));
            // Test 5: Single character
            Assert(isEqual(exchange.urlencodeBase64("a"), "YQ"));
            // Test 6: Two characters
            Assert(isEqual(exchange.urlencodeBase64("ab"), "YWI"));
            // Test 7: Three characters (no padding needed)
            Assert(isEqual(exchange.urlencodeBase64("abc"), "YWJj"));
            // Test 8: Four characters
            Assert(isEqual(exchange.urlencodeBase64("abcd"), "YWJjZA"));
            // Test 9: JSON-like string
            Assert(isEqual(exchange.urlencodeBase64("{\"user\":\"test\"}"), "eyJ1c2VyIjoidGVzdCJ9"));
            // Test 10: String with special characters (urlsafe base64)
            Assert(isEqual(exchange.urlencodeBase64("subjects?_d"), "c3ViamVjdHM_X2Q"));
            // Test 11: Longer string
            Assert(isEqual(exchange.urlencodeBase64("The quick brown fox"), "VGhlIHF1aWNrIGJyb3duIGZveA"));
            // Test 12: Numbers as string
            Assert(isEqual(exchange.urlencodeBase64("123456789"), "MTIzNDU2Nzg5"));
        }
}