package base

import ccxt "github.com/ccxt/ccxt/go/v4"

// PLEASE DO NOT EDIT THIS FILE, IT IS GENERATED AND WILL BE OVERWRITTEN:
// https://github.com/ccxt/ccxt/blob/master/CONTRIBUTING.md#how-to-contribute-code

func TestPrecisionFromString() {
	exchange := ccxt.NewExchange().(*ccxt.Exchange)
	exchange.DerivedExchange = exchange
	exchange.InitParent(map[string]interface{}{
		"id": "sampleexchange",
	}, map[string]interface{}{}, exchange)
	// Test 1: Scientific notation negative exponent
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("1e-4"), 4))
	// Test 2: Scientific notation uppercase E
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("1E-4"), 4))
	// Test 3: Scientific notation larger negative exponent
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("1e-8"), 8))
	// Test 4: Scientific notation with decimal
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("2.5e-6"), 6))
	// Test 5: Scientific notation positive exponent
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("1e4"), ccxt.OpNeg(4)))
	// Test 6: Scientific notation explicit positive
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("1e+4"), ccxt.OpNeg(4)))
	// Test 7: Decimal string 4 places
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("0.0001"), 4))
	// Test 8: Decimal string 5 places
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("0.00001"), 5))
	// Test 9: Decimal string 1 place
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("0.1"), 1))
	// Test 10: Decimal string 2 places
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("0.01"), 2))
	// Test 11: Decimal string 8 places
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("0.00000001"), 8))
	// Test 12: Trailing zeros should be stripped
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("0.0100"), 2))
	// Test 13: Trailing zeros stripped
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("0.00100"), 3))
	// Test 14: All trailing zeros after decimal
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("1.0000"), 0))
	// Test 15: Integer string (no decimal)
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("1"), 0))
	// Test 16: Integer string larger
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("10"), 0))
	// Test 17: Integer string even larger
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("100"), 0))
	// Test 18: Zero with decimal
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("0.0"), 0))
	// Test 19: One with decimal
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("1.0"), 0))
	// Test 20: Mixed precision
	Assert(ccxt.IsEqual(exchange.PrecisionFromString("0.12345"), 5))
}
