    public function fetch_ohlcv(string $symbol, $timeframe = '1m', ?int $since = null, ?int $limit = null, $params = array ()): array {
        /**
         * fetches historical candlestick data containing the open, high, low, and close $price, and the volume of a $market
         *
         * @see https://github.com/asterdex/api-docs/blob/master/aster-finance-spot-api.md#k-line-data
         * @see https://github.com/asterdex/api-docs/blob/master/aster-finance-futures-api.md#klinecandlestick-data
         *
         * @param {string} $symbol unified $symbol of the $market to fetch OHLCV data for
         * @param {string} $timeframe the length of time each candle represents
         * @param {int} [$since] timestamp in ms of the earliest candle to fetch
         * @param {int} [$limit] the maximum amount of candles to fetch
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @param {string} [$params->price] "mark" or "index" for mark $price and index $price candles
         * @param {int} [$params->until] the latest time in ms to fetch orders for
         * @return {int[][]} A list of candles ordered, open, high, low, close, volume
         */
        if ($symbol === null) {
            throw new ArgumentsRequired($this->id . ' fetchOHLCV() requires a $symbol argument');
        }
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array();
        if ($since !== null) {
            $request['startTime'] = $since;
        }
        if ($limit !== null) {
            if ($limit > 1500) {
                $limit = 1500; // Default 500; max 1500.
            }
            $request['limit'] = $limit;
        }
        list($request, $params) = $this->handle_until_option('endTime', $request, $params);
        $request['interval'] = $this->safe_string($this->timeframes, $timeframe, $timeframe);
        $price = $this->safe_string($params, 'price');
        $isMark = ($price === 'mark');
        $isIndex = ($price === 'index');
        $params = $this->omit($params, 'price');
        $response = null;
        if ($isMark) {
            $request['symbol'] = $market['id'];
            $response = $this->fapiPublicGetV1MarkPriceKlines ($this->extend($request, $params));
        } elseif ($isIndex) {
            $request['pair'] = $market['id'];
            $response = $this->fapiPublicGetV1IndexPriceKlines ($this->extend($request, $params));
        } else {
            $request['symbol'] = $market['id'];
            if ($market['linear']) {
                $response = $this->fapiPublicGetV1Klines ($this->extend($request, $params));
            } else {
                $response = $this->sapiPublicGetV1Klines ($this->extend($request, $params));
            }
        }
        //
        //     array(
        //         array(
        //             1631158560000,
        //             "208.1850",
        //             "208.1850",
        //             "208.1850",
        //             "208.1850",
        //             "11.84",
        //             1631158619999,
        //             "2464.910400",
        //             1,
        //             "11.84",
        //             "2464.910400",
        //             "0"
        //         )
        //     )
        //
        return $this->parse_ohlcvs($response, $market, $timeframe, $since, $limit);
    }

    public function parse_trade(array $trade, ?array $market = null): array {
        //
        // fetchTrades
        //
        //     {
        //         "id" => 3913206,
        //         "price" => "644.100",
        //         "qty" => "0.08",
        //         "quoteQty" => "51.528",
        //         "time" => 1749784506633,
        //         "isBuyerMaker" => true
        //     }
        //
        //     {
        //         "id" => 657,
        //         "price" => "1.01000000",
        //         "qty" => "5.00000000",
        //         "baseQty" => "4.95049505",
        //         "time" => 1755156533943,
        //         "isBuyerMaker" => false
        //     }
        //
        // fetchMyTrades
        //
        //     {
        //         "buyer" => false,
        //         "commission" => "-0.07819010",
        //         "commissionAsset" => "USDT",
        //         "id" => 698759,
        //         "maker" => false,
        //         "orderId" => 25851813,
        //         "price" => "7819.01",
        //         "qty" => "0.002",
        //         "quoteQty" => "15.63802",
        //         "realizedPnl" => "-0.91539999",
        //         "side" => "SELL",
        //         "positionSide" => "SHORT",
        //         "symbol" => "BTCUSDT",
        //         "time" => 1569514978020
        //     }
        //
        $id = $this->safe_string($trade, 'id');
        $symbol = $market['symbol'];
        $currencyId = $this->safe_string($trade, 'commissionAsset');
        $currencyCode = $this->safe_currency_code($currencyId);
        $amountString = $this->safe_string($trade, 'qty');
        $priceString = $this->safe_string($trade, 'price');
        $costString = $this->safe_string_2($trade, 'quoteQty', 'baseQty');
        $timestamp = $this->safe_integer($trade, 'time');
        $side = $this->safe_string_lower($trade, 'side');
        $isMaker = $this->safe_bool($trade, 'maker');
        $takerOrMaker = null;
        if ($isMaker !== null) {
            $takerOrMaker = $isMaker ? 'maker' : 'taker';
        }
        $isBuyerMaker = $this->safe_bool($trade, 'isBuyerMaker');
        if ($isBuyerMaker !== null) {
            $side = $isBuyerMaker ? 'sell' : 'buy';
        }
        return $this->safe_trade(array(
            'id' => $id,
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'order' => $this->safe_string($trade, 'orderId'),
            'type' => null,
            'side' => $side,
            'takerOrMaker' => $takerOrMaker,
            'price' => $priceString,
            'amount' => $amountString,
            'cost' => $costString,
            'fee' => array(
                'cost' => $this->parse_number(Precise::string_abs($this->safe_string($trade, 'commission'))),
                'currency' => $currencyCode,
            ),
        ), $market);
    }

