    public function parse_order(array $order, ?array $market = null): array {
        //
        // swap
        //     {
        //         "avgPrice" => "0.00000",
        //         "clientOrderId" => "abc",
        //         "cumQuote" => "0",
        //         "executedQty" => "0",
        //         "orderId" => 1917641,
        //         "origQty" => "0.40",
        //         "origType" => "TRAILING_STOP_MARKET",
        //         "price" => "0",
        //         "reduceOnly" => false,
        //         "side" => "BUY",
        //         "positionSide" => "SHORT",
        //         "status" => "NEW",
        //         "stopPrice" => "9300",
        //         "closePosition" => false,
        //         "symbol" => "BTCUSDT",
        //         "time" => 1579276756075,
        //         "timeInForce" => "GTC",
        //         "type" => "TRAILING_STOP_MARKET",
        //         "activatePrice" => "9020",
        //         "priceRate" => "0.3",
        //         "updateTime" => 1579276756075,
        //         "workingType" => "CONTRACT_PRICE",
        //         "priceProtect" => false
        //     }
        // spot
        //     {
        //         "orderId" => 38,
        //         "symbol" => "ADA25SLP25",
        //         "status" => "FILLED",
        //         "clientOrderId" => "afMd4GBQyHkHpGWdiy34Li",
        //         "price" => "20",
        //         "avgPrice" => "12.0000000000000000",
        //         "origQty" => "10",
        //         "executedQty" => "10",
        //         "cumQuote" => "120",
