    public function create_order(string $symbol, string $type, string $side, float $amount, ?float $price = null, $params = array ()) {
        /**
         * create a trade order
         *
         * @see https://github.com/asterdex/api-docs/blob/master/aster-finance-spot-api.md#place-order-trade
         * @see https://github.com/asterdex/api-docs/blob/master/aster-finance-futures-api.md#new-order--trade
         *
         * @param {string} $symbol unified $symbol of the $market to create an order in
         * @param {string} $type 'market' or 'limit' or 'STOP' or 'STOP_MARKET' or 'TAKE_PROFIT' or 'TAKE_PROFIT_MARKET' or 'TRAILING_STOP_MARKET'
         * @param {string} $side 'buy' or 'sell'
         * @param {float} $amount how much of you want to trade in units of the base currency
         * @param {float} [$price] the $price that the order is to be fulfilled, in units of the quote currency, ignored in $market orders
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @param {string} [$params->reduceOnly] for swap and future reduceOnly is a string 'true' or 'false' that cant be sent with close position set to true or in hedge mode. For spot margin and option reduceOnly is a boolean.
         * @param {boolean} [$params->test] whether to use the $test endpoint or not, default is false
         * @param {float} [$params->trailingPercent] the percent to trail away from the current $market $price
         * @param {float} [$params->trailingTriggerPrice] the $price to trigger a trailing order, default uses the $price argument
         * @param {string} [$params->positionSide] "BOTH" for one-way mode, "LONG" for buy $side of hedged mode, "SHORT" for sell $side of hedged mode
         * @param {float} [$params->triggerPrice] the $price that a trigger order is triggered at
         * @param {float} [$params->stopLossPrice] the $price that a stop loss order is triggered at
         * @param {float} [$params->takeProfitPrice] the $price that a take profit order is triggered at
         * @return {array} an ~@link https://docs.ccxt.com/#/?id=order-structure order structure~
         */
        $this->load_markets();
        $market = $this->market($symbol);
        $test = $this->safe_bool($params, 'test', false);
        $params = $this->omit($params, 'test');
        $request = $this->create_order_request($symbol, $type, $side, $amount, $price, $params);
        $response = null;
        if ($market['swap']) {
            if ($test) {
                $response = $this->fapiPrivatePostV1OrderTest ($request);
            } else {
                $response = $this->fapiPrivatePostV1Order ($request);
            }
        } else {
            $response = $this->sapiPrivatePostV1Order ($request);
        }
        return $this->parse_order($response, $market);
    }

    public function create_orders(array $orders, $params = array ()) {
        /**
         * create a list of trade $orders
         *
         * @see https://github.com/asterdex/api-docs/blob/master/aster-finance-futures-api.md#place-multiple-$orders--trade
         *
         * @param {Array} $orders list of $orders to create, each object should contain the parameters required by createOrder, namely symbol, $type, $side, $amount, $price and $params
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} an ~@link https://docs.ccxt.com/#/?id=order-structure order structure~
         */
        $this->load_markets();
        $ordersRequests = array();
        $orderSymbols = array();
        if (strlen($orders) > 5) {
            throw new InvalidOrder($this->id . ' createOrders() order list max 5 orders');
        }
        for ($i = 0; $i < count($orders); $i++) {
            $rawOrder = $orders[$i];
            $marketId = $this->safe_string($rawOrder, 'symbol');
            $type = $this->safe_string($rawOrder, 'type');
            $side = $this->safe_string($rawOrder, 'side');
            $amount = $this->safe_value($rawOrder, 'amount');
            $price = $this->safe_value($rawOrder, 'price');
            $orderParams = $this->safe_dict($rawOrder, 'params', array());
            $orderRequest = $this->create_order_request($marketId, $type, $side, $amount, $price, $orderParams);
            $ordersRequests[] = $orderRequest;
        }
        $orderSymbols = $this->market_symbols($orderSymbols, null, false, true, true);
        $market = $this->market($orderSymbols[0]);
        if ($market['spot']) {
            throw new NotSupported($this->id . ' createOrders() does not support ' . $market['type'] . ' orders');
        }
        $request = array(
            'batchOrders' => $ordersRequests,
        );
        $response = $this->fapiPrivatePostV1BatchOrders ($this->extend($request, $params));
        return $this->parse_orders($response);
    }

    public function create_order_request(string $symbol, string $type, string $side, float $amount, ?float $price = null, $params = array ()) {
        /**
         * @ignore
         * helper function to build the $request
         * @param {string} $symbol unified $symbol of the $market to create an order in
         * @param {string} $type 'market' or 'limit'
         * @param {string} $side 'buy' or 'sell'
         * @param {float} $amount how much you want to trade in units of the base currency
         * @param {float} [$price] the $price that the order is to be fulfilled, in units of the quote currency, ignored in $market orders
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} $request to be sent to the exchange
         */
        $market = $this->market($symbol);
        $initialUppercaseType = strtoupper($type);
        $isMarketOrder = $initialUppercaseType === 'MARKET';
        $isLimitOrder = $initialUppercaseType === 'LIMIT';
        $request = array(
            'symbol' => $market['id'],
            'side' => strtoupper($side),
        );
        $clientOrderId = $this->safe_string_2($params, 'newClientOrderId', 'clientOrderId');
        if ($clientOrderId !== null) {
            $request['newClientOrderId'] = $clientOrderId;
        }
        $triggerPrice = $this->safe_string_2($params, 'triggerPrice', 'stopPrice');
        $stopLossPrice = $this->safe_string($params, 'stopLossPrice', $triggerPrice);
        $takeProfitPrice = $this->safe_string($params, 'takeProfitPrice');
        $trailingDelta = $this->safe_string($params, 'trailingDelta');
        $trailingTriggerPrice = $this->safe_string_2($params, 'trailingTriggerPrice', 'activationPrice');
        $trailingPercent = $this->safe_string_n($params, array( 'trailingPercent', 'callbackRate', 'trailingDelta' ));
        $isTrailingPercentOrder = $trailingPercent !== null;
        $isStopLoss = $stopLossPrice !== null || $trailingDelta !== null;
        $isTakeProfit = $takeProfitPrice !== null;
        $uppercaseType = $initialUppercaseType;
        $stopPrice = null;
        if ($isTrailingPercentOrder) {
            if ($market['swap']) {
                $uppercaseType = 'TRAILING_STOP_MARKET';
                $request['callbackRate'] = $trailingPercent;
                if ($trailingTriggerPrice !== null) {
                    $request['activationPrice'] = $this->price_to_precision($symbol, $trailingTriggerPrice);
                }
            }
        } elseif ($isStopLoss) {
            $stopPrice = $stopLossPrice;
            if ($isMarketOrder) {
                $uppercaseType = 'STOP_MARKET';
            } elseif ($isLimitOrder) {
                $uppercaseType = 'STOP';
            }
        } elseif ($isTakeProfit) {
            $stopPrice = $takeProfitPrice;
            if ($isMarketOrder) {
                $uppercaseType = 'TAKE_PROFIT_MARKET';
            } elseif ($isLimitOrder) {
                $uppercaseType = 'TAKE_PROFIT';
            }
        }
        $postOnly = $this->is_post_only($isMarketOrder, null, $params);
        if ($postOnly) {
            $request['timeInForce'] = 'GTX';
        }
        //
        // spot
        // LIMIT timeInForce, quantity, $price
        // MARKET quantity or $quoteOrderQty
        // STOP and TAKE_PROFIT quantity, $price, $stopPrice
        // STOP_MARKET and TAKE_PROFIT_MARKET quantity, $stopPrice
        // future
        // LIMIT timeInForce, quantity, $price
        // MARKET quantity
        // STOP/TAKE_PROFIT quantity, $price, $stopPrice
        // STOP_MARKET/TAKE_PROFIT_MARKET $stopPrice
        // TRAILING_STOP_MARKET callbackRate
        //
        // additional required fields depending on the order $type
        $closePosition = $this->safe_bool($params, 'closePosition', false);
        $timeInForceIsRequired = false;
        $priceIsRequired = false;
        $triggerPriceIsRequired = false;
        $quantityIsRequired = false;
        $request['type'] = $uppercaseType;
        if ($uppercaseType === 'MARKET') {
            if ($market['spot']) {
                $quoteOrderQty = $this->safe_bool($this->options, 'quoteOrderQty', true);
                if ($quoteOrderQty) {
                    $quoteOrderQtyNew = $this->safe_string_2($params, 'quoteOrderQty', 'cost');
                    $precision = $market['precision']['price'];
                    if ($quoteOrderQtyNew !== null) {
                        $request['quoteOrderQty'] = $this->decimal_to_precision($quoteOrderQtyNew, TRUNCATE, $precision, $this->precisionMode);
                    } elseif ($price !== null) {
                        $amountString = $this->number_to_string($amount);
                        $priceString = $this->number_to_string($price);
                        $quoteOrderQuantity = Precise::string_mul($amountString, $priceString);
                        $request['quoteOrderQty'] = $this->decimal_to_precision($quoteOrderQuantity, TRUNCATE, $precision, $this->precisionMode);
                    } else {
                        $quantityIsRequired = true;
                    }
                } else {
                    $quantityIsRequired = true;
                }
            } else {
                $quantityIsRequired = true;
            }
        } elseif ($uppercaseType === 'LIMIT') {
            $timeInForceIsRequired = true;
            $quantityIsRequired = true;
            $priceIsRequired = true;
        } elseif (($uppercaseType === 'STOP') || ($uppercaseType === 'TAKE_PROFIT')) {
            $quantityIsRequired = true;
            $priceIsRequired = true;
            $triggerPriceIsRequired = true;
        } elseif (($uppercaseType === 'STOP_MARKET') || ($uppercaseType === 'TAKE_PROFIT_MARKET')) {
            if (!$closePosition) {
                $quantityIsRequired = true;
            }
            $triggerPriceIsRequired = true;
        } elseif ($uppercaseType === 'TRAILING_STOP_MARKET') {
            $request['callbackRate'] = $trailingPercent;
            if ($trailingTriggerPrice !== null) {
                $request['activationPrice'] = $this->price_to_precision($symbol, $trailingTriggerPrice);
            }
        }
        if ($quantityIsRequired) {
            $marketAmountPrecision = $this->safe_string($market['precision'], 'amount');
            $isPrecisionAvailable = ($marketAmountPrecision !== null);
            if ($isPrecisionAvailable) {
                $request['quantity'] = $this->amount_to_precision($symbol, $amount);
            } else {
                $request['quantity'] = $this->parse_to_numeric($amount);
            }
        }
        if ($priceIsRequired) {
            if ($price === null) {
                throw new InvalidOrder($this->id . ' createOrder() requires a $price argument for a ' . $type . ' order');
            }
            $pricePrecision = $this->safe_string($market['precision'], 'price');
            $isPricePrecisionAvailable = ($pricePrecision !== null);
            if ($isPricePrecisionAvailable) {
                $request['price'] = $this->price_to_precision($symbol, $price);
            } else {
                $request['price'] = $this->parse_to_numeric($price);
            }
        }
        if ($triggerPriceIsRequired) {
            if ($stopPrice === null) {
                throw new InvalidOrder($this->id . ' createOrder() requires a $stopPrice extra param for a ' . $type . ' order');
            }
            if ($stopPrice !== null) {
                $request['stopPrice'] = $this->price_to_precision($symbol, $stopPrice);
            }
        }
        if ($timeInForceIsRequired && ($this->safe_string($params, 'timeInForce') === null) && ($this->safe_string($request, 'timeInForce') === null)) {
            $request['timeInForce'] = $this->safe_string($this->options, 'defaultTimeInForce'); // 'GTC' = Good To Cancel (default), 'IOC' = Immediate Or Cancel
        }
        $requestParams = $this->omit($params, array( 'newClientOrderId', 'clientOrderId', 'stopPrice', 'triggerPrice', 'trailingTriggerPrice', 'trailingPercent', 'trailingDelta', 'stopPrice', 'stopLossPrice', 'takeProfitPrice' ));
        return $this->extend($request, $requestParams);
    }

