        //     {
        //         "amount" => 100.0,
        //         "code" => 200,
        //         "msg" => "Successfully modify position margin.",
        //         "type" => 1
        //     }
        //
        return $this->extend($this->parse_margin_modification($response, $market), array(
            'code' => $code,
