    public function nonce() {
        // AsterDEX uses microsecond precision for nonce
        return intval($this->milliseconds() * 1000);
    }

    public function encode_message($params, $user, $signer, $nonce) {
        // Convert params to JSON string (sorted keys, no whitespace)
        $params_json = $this->json($params);

        // Concatenate: paramsJson + user + signer + nonce
        $message = $params_json . strtolower($user) . strtolower($signer) . strval($nonce);

        // Hash with keccak256
        // Note: In production, you would use a proper keccak256 implementation
        // For now, using hash as a placeholder
        return hash('sha3-256', $message, true);
    }

    public function sign_hash($hash, $private_key) {
        // Remove 0x prefix if present
        $clean_private_key = str_replace('0x', '', $private_key);

        // This is a placeholder implementation
        // In production, use proper ECDSA secp256k1 library
        throw new NotSupported($this->id . ' sign_hash() requires ECDSA secp256k1 library for signatures');
    }

    public function create_signature($params, $user, $signer, $nonce) {
        $hash = $this->encode_message($params, $user, $signer, $nonce);
        $signature = $this->sign_hash($hash, $this->privateKey);
        return $signature;
    }

    public function sign($path, $api = 'public', $method = 'GET', $params = array(), $headers = null, $body = null) {
        $is_private = ($api === 'private') || ($api === 'fapiPrivate') || ($api === 'sapiPrivate');

        if (!array_key_exists($api, $this->urls['api'])) {
            throw new NotSupported($this->id . ' does not have URL for ' . $api . ' endpoints');
        }

        $url = $this->urls['api'][$api];
        $url .= '/' . $path;

        if ($is_private) {
            $this->check_required_credentials();

            $nonce = $this->nonce();
            $timestamp = $this->milliseconds();
            $recv_window = $this->safe_integer($this->options, 'recvWindow', 50000);

            // Extend params with timestamp and recvWindow
            $extended_params = $this->extend(array(
                'timestamp' => $timestamp,
                'recvWindow' => $recv_window,
            ), $params);

            // Create signature
            $user = $this->walletAddress;
            $signer = $this->walletAddress;
            $signature = $this->create_signature($extended_params, $user, $signer, $nonce);

            // Add authentication parameters
            $extended_params['user'] = $user;
            $extended_params['signer'] = $signer;
            $extended_params['nonce'] = $nonce;
            $extended_params['signature'] = $signature;

            $query = $this->urlencode($extended_params);

            $headers = array(
                'Content-Type' => 'application/x-www-form-urlencoded',
            );

            if (($method === 'GET') || ($method === 'DELETE')) {
                $url .= '?' . $query;
            } else {
                $body = $query;
            }
        } else {
            if (count($params)) {
                $url .= '?' . $this->urlencode($params);
            }
        }

        return array('url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers);
