    public function sign($path, $api = 'public', $method = 'GET', $params = array (), $headers = null, $body = null) {
        $url = '/' . $this->implode_params($path, $params);
        $query = $this->omit($params, $this->extract_params($path));
        if ($headers === null) {
            $headers = array();
        }
        if ($api === 'private') {
            $this->check_required_credentials();
            $nonce = $this->milliseconds();
            $auth = 'ACE_SIGN' . $this->secret;
            $data = $this->extend(array(
                'apiKey' => $this->apiKey,
                'timeStamp' => $this->number_to_string($nonce),
            ), $params);
            $sortedData = $this->keysort($data);
            $values = is_array($sortedData) ? array_values($sortedData) : array();
            $stringifiedValues = array();
            for ($i = 0; $i < count($values); $i++) {
                $value = $values[$i];
                $strValue = (string) $value;
                $stringifiedValues[] = $strValue;
            }
            $auth .= implode('', $stringifiedValues);
            $signature = $this->hash($this->encode($auth), 'sha256', 'hex');
            $data['signKey'] = $signature;
            $headers = array(
                'Content-Type' => 'application/x-www-form-urlencoded',
            );
            if ($method === 'POST') {
                $brokerId = $this->safe_string($this->options, 'brokerId');
                if ($brokerId !== null) {
                    $headers['Referer'] = $brokerId;
                }
            }
            $body = $this->urlencode($data);
        } elseif ($api === 'public' && $method === 'GET') {
            if ($query) {
                $url .= '?' . $this->urlencode($query);
            }
        }
        $url = $this->urls['api'][$api] . $url;
        return array( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }

    public function handle_errors(int $code, string $reason, string $url, string $method, array $headers, string $body, $response, $requestHeaders, $requestBody) {
        if ($response === null) {
            return null; // fallback to the default error handler
        }
        $feedback = $this->id . ' ' . $body;
        $status = $this->safe_number($response, 'status', 200);
        if ($status > 200) {
            $statusStr = (string) $status;
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $statusStr, $feedback);
            $this->throw_broadly_matched_exception($this->exceptions['broad'], $statusStr, $feedback);
        }
        return null;
    }
