    public function fetch_currencies($params = array ()): PromiseInterface {
        return Async\async(function () use ($params) {
            /**
             * fetches all available currencies on an exchange
             *
             * @see https://arkm.com/docs#get/public/assets
             *
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} an associative dictionary of currencies
             */
            $response = Async\await($this->v1PublicGetAssets ($params));
            //
            //    array(
            //        {
            //            "symbol" => "USDT",
            //            "name" => "Tether",
            //            "imageUrl" => "https://static.arkhamintelligence.com/tokens/tether.png",
            //            "stablecoin" => true,
            //            "featuredPair" => "BTC_USDT",
            //            "chains" => [
            //                array(
            //                    "symbol" => "ETH",
            //                    "assetSymbol" => "ETH",
            //                    "name" => "Ethereum",
            //                    "type" => "1",
            //                    "confirmations" => "6",
            //                    "blockTime" => "12000000"
            //                }
            //            ),
            //            "status" => "listed",
            //            "minDeposit" => "5",
            //            "minWithdrawal" => "5",
            //            "withdrawalFee" => "2"
            //        ),
            //        ...
            //
            $result = array();
            for ($i = 0; $i < count($response); $i++) {
                $currency = $response[$i];
                $id = $this->safe_string($currency, 'symbol');
                $code = $this->safe_currency_code($id);
                $networks = array();
                $chains = $this->safe_list($currency, 'chains', array());
                for ($j = 0; $j < count($chains); $j++) {
                    $chain = $chains[$j];
                    $networkId = $this->safe_string($chain, 'symbol');
                    $network = $this->network_id_to_code($networkId);
                    $networks[$network] = array(
                        'info' => $chain,
                        'id' => $networkId,
                        'network' => $network,
                        'title' => $this->safe_string($chain, 'name'),
                        'active' => null,
                        'deposit' => null,
                        'withdraw' => null,
                        'fee' => null,
                        'precision' => null,
                        'limits' => array(
                            'withdraw' => array(
                                'min' => null,
                                'max' => null,
                            ),
                        ),
                    );
                }
                $result[$code] = $this->safe_currency_structure(array(
                    'info' => $currency,
                    'id' => $id,
                    'code' => $code,
                    'name' => $this->safe_string($currency, 'name'),
                    'active' => $this->safe_string($currency, 'status') === 'listed',
                    'deposit' => null,
                    'withdraw' => null,
                    'fee' => $this->safe_number($currency, 'withdrawalFee'),
                    'precision' => null,
                    'limits' => array(
                        'amount' => array(
                            'min' => null,
                            'max' => null,
                        ),
                        'withdraw' => array(
                            'min' => $this->safe_number($currency, 'minWithdrawal'),
                            'max' => null,
                        ),
                        'deposit' => array(
                            'min' => $this->safe_number($currency, 'minDeposit'),
                            'max' => null,
                        ),
                    ),
                    'type' => 'crypto',
                    'networks' => $networks,
                ));
            }
            return $result;
        }) ();
    }

