    public function fetch_order_book(string $symbol, ?int $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $limit, $params) {
            /**
             * fetches information on open orders with bid (buy) and ask (sell) prices, volumes and other data
             * @see https://binance-docs.github.io/apidocs/spot/en/#order-book      // spot
             * @see https://binance-docs.github.io/apidocs/futures/en/#order-book   // swap
             * @see https://binance-docs.github.io/apidocs/delivery/en/#order-book  // future
             * @see https://binance-docs.github.io/apidocs/voptions/en/#order-book  // option
             * @param {string} $symbol unified $symbol of the $market to fetch the order book for
             * @param {int|null} $limit the maximum amount of order book entries to return
             * @param {array} $params extra parameters specific to the binance api endpoint
             * @return {array} A dictionary of ~@link https://docs.ccxt.com/#/?id=order-book-structure order book structures~ indexed by $market symbols
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
            );
            if ($limit !== null) {
                $request['limit'] = $limit; // default 100, max 5000, see https://github.com/binance/binance-spot-api-docs/blob/master/rest-api.md#order-book
            }
            $method = 'publicGetDepth';
            if ($market['option']) {
                $method = 'eapiPublicGetDepth';
            } elseif ($market['linear']) {
                $method = 'fapiPublicGetDepth';
            } elseif ($market['inverse']) {
                $method = 'dapiPublicGetDepth';
            }
            $response = Async\await($this->$method (array_merge($request, $params)));
            //
            // future
            //
            //     {
            //         "lastUpdateId":333598053905,
            //         "E":1618631511986,
            //         "T":1618631511964,
            //         "bids":[
            //             ["2493.56","20.189"],
            //             ["2493.54","1.000"],
            //             ["2493.51","0.005"]
            //         ],
            //         "asks":[
            //             ["2493.57","0.877"],
            //             ["2493.62","0.063"],
            //             ["2493.71","12.054"],
            //         ]
            //     }
            //
            // options (eapi)
            //
            //     {
            //         "bids" => [
            //             ["108.7","16.08"],
            //             ["106","21.29"],
            //             ["82.4","0.02"]
            //         ],
            //         "asks" => [
            //             ["111.4","19.52"],
            //             ["119.9","17.6"],
            //             ["141.2","31"]
            //         ],
            //         "T" => 1676771382078,
            //         "u" => 1015939
            //     }
            //
            $timestamp = $this->safe_integer($response, 'T');
            $orderbook = $this->parse_order_book($response, $symbol, $timestamp);
            $orderbook['nonce'] = $this->safe_integer_2($response, 'lastUpdateId', 'u');
            return $orderbook;
        }) ();
    }

