        //
        // eapi => fetchTicker, fetchTickers
        //
        //     {
        //         "symbol" => "ETH-230510-1825-C",
        //         "priceChange" => "-5.1",
        //         "priceChangePercent" => "-0.1854",
        //         "lastPrice" => "22.4",
