        //         1591256519999,          // Close time
        //         "0",                    // Ignore
        //         60,                     // Number of bisic data
        //         "0",                    // Ignore
        //         "0",                    // Ignore
        //         "0"                     // Ignore
        //         )
        //     )
        //
        // options
        //
        //     {
        //         "open" => "32.2",
        //         "high" => "32.2",
        //         "low" => "32.2",
        //         "close" => "32.2",
        //         "volume" => "0",
        //         "interval" => "5m",
        //         "tradeCount" => 0,
        //         "takerVolume" => "0",
        //         "takerAmount" => "0",
        //         "amount" => "0",
        //         "openTime" => 1677096900000,
        //         "closeTime" => 1677097200000
