    public function parse_market_leverage_tiers($info, $market = null) {
        /**
         * @ignore
         * @param {array} $info Exchange response for 1 $market
         * @param {array} $market CCXT $market
         */
        //
        //    {
        //        "symbol" => "SUSHIUSDT",
