    public function parse_market(array $market): array {
        $id = $this->safe_string($market, 'symbol');
        $symbolParts = explode('-', $id);
        $baseId = $symbolParts[0];
        $quoteId = $symbolParts[1];
        $base = $this->safe_currency_code($baseId);
        $quote = $this->safe_currency_code($quoteId);
        $currency = $this->safe_string($market, 'currency');
        $checkIsInverse = false;
        $checkIsLinear = true;
        $minTickSize = $this->safe_number($market, 'minTickSize');
        if ($minTickSize !== null) {
            // inverse $swap $market
            $currency = $baseId;
            $checkIsInverse = true;
            $checkIsLinear = false;
        }
        $settle = $this->safe_currency_code($currency);
        $pricePrecision = $this->safe_number($market, 'tickSize');
        if ($pricePrecision === null) {
            $pricePrecision = $this->parse_number($this->parse_precision($this->safe_string($market, 'pricePrecision')));
        }
        $quantityPrecision = $this->safe_number($market, 'stepSize');
        if ($quantityPrecision === null) {
            $quantityPrecision = $this->parse_number($this->parse_precision($this->safe_string($market, 'quantityPrecision')));
        }
        $type = ($settle !== null) ? 'swap' : 'spot';
        $spot = $type === 'spot';
        $swap = $type === 'swap';
        $symbol = $base . '/' . $quote;
        if ($settle !== null) {
            $symbol .= ':' . $settle;
        }
        $fees = $this->safe_dict($this->fees, $type, array());
        $contractSize = ($swap) ? $this->parse_number('1') : null;
        $isActive = false;
        if (($this->safe_string($market, 'apiStateOpen') === 'true') && ($this->safe_string($market, 'apiStateClose') === 'true')) {
            $isActive = true; // $swap active
        } elseif ($this->safe_bool($market, 'apiStateSell') && $this->safe_bool($market, 'apiStateBuy') && ($this->safe_string($market, 'status') === '1')) {
            $isActive = true; // $spot active
        }
        $isInverse = ($spot) ? null : $checkIsInverse;
        $isLinear = ($spot) ? null : $checkIsLinear;
        $minAmount = null;
        if (!$spot) {
            $minAmount = $this->safe_number_2($market, 'minQty', 'tradeMinQuantity');
        }
        $timeOnline = $this->safe_integer($market, 'timeOnline');
        if ($timeOnline === 0) {
            $timeOnline = null;
        }
        return $this->safe_market_structure(array(
            'id' => $id,
            'symbol' => $symbol,
            'base' => $base,
            'quote' => $quote,
            'settle' => $settle,
            'baseId' => $baseId,
            'quoteId' => $quoteId,
            'settleId' => $currency,
            'type' => $type,
            'spot' => $spot,
            'margin' => false,
            'swap' => $swap,
            'future' => false,
            'option' => false,
            'active' => $isActive,
            'contract' => $swap,
            'linear' => $isLinear,
            'inverse' => $isInverse,
            'taker' => $this->safe_number($fees, 'taker'),
            'maker' => $this->safe_number($fees, 'maker'),
            'feeSide' => $this->safe_string($fees, 'feeSide'),
            'contractSize' => $contractSize,
            'expiry' => null,
            'expiryDatetime' => null,
            'strike' => null,
            'optionType' => null,
            'precision' => array(
                'amount' => $quantityPrecision,
                'price' => $pricePrecision,
            ),
            'limits' => array(
                'leverage' => array(
                    'min' => null,
                    'max' => null,
                ),
                'amount' => array(
                    'min' => $minAmount,
                    'max' => null,
                ),
                'price' => array(
                    'min' => $minTickSize,
                    'max' => null,
                ),
                'cost' => array(
                    'min' => $this->safe_number_n($market, array( 'minNotional', 'tradeMinUSDT', 'minTradeValue' )),
                    'max' => $this->safe_number($market, 'maxNotional'),
                ),
            ),
            'created' => $timeOnline,
            'info' => $market,
        ));
    }

    public function fetch_markets($params = array ()): PromiseInterface {
        return Async\async(function () use ($params) {
            /**
             * retrieves data on all markets for bingx
             *
             * @see https://bingx-api.github.io/docs/#/spot/market-api.html#Query%20Symbols
             * @see https://bingx-api.github.io/docs/#/swapV2/market-api.html#Contract%20Information
             * @see https://bingx-api.github.io/docs/#/en-us/cswap/market-api.html#Contract%20Information
             *
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array[]} an array of objects representing market data
             */
            $requests = array( $this->fetch_swap_markets($params) );
            $isSandbox = $this->safe_bool($this->options, 'sandboxMode', false);
            if (!$isSandbox) {
                $requests[] = $this->fetch_inverse_swap_markets($params);
                $requests[] = $this->fetch_spot_markets($params); // sandbox is swap only
            }
            $promises = Async\await(Promise\all($requests));
            $linearSwapMarkets = $this->safe_list($promises, 0, array());
            $inverseSwapMarkets = $this->safe_list($promises, 1, array());
            $spotMarkets = $this->safe_list($promises, 2, array());
            $swapMarkets = $this->array_concat($linearSwapMarkets, $inverseSwapMarkets);
            return $this->array_concat($spotMarkets, $swapMarkets);
        }) ();
    }

    public function fetch_ohlcv(string $symbol, $timeframe = '1m', ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $timeframe, $since, $limit, $params) {
            /**
             * fetches historical candlestick data containing the open, high, low, and close $price, and the volume of a $market
             *
             * @see https://bingx-api.github.io/docs/#/swapV2/market-api.html#K-Line%20Data
             * @see https://bingx-api.github.io/docs/#/spot/market-api.html#Candlestick%20chart%20data
             * @see https://bingx-api.github.io/docs/#/swapV2/market-api.html#%20K-Line%20Data
             * @see https://bingx-api.github.io/docs/#/en-us/swapV2/market-api.html#Mark%20Price%20Kline/Candlestick%20Data
             * @see https://bingx-api.github.io/docs/#/en-us/cswap/market-api.html#Get%20K-line%20Data
             *
             * @param {string} $symbol unified $symbol of the $market to fetch OHLCV data for
             * @param {string} $timeframe the length of time each candle represents
             * @param {int} [$since] timestamp in ms of the earliest candle to fetch
             * @param {int} [$limit] the maximum amount of candles to fetch
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @param {int} [$params->until] timestamp in ms of the latest candle to fetch
             * @param {boolean} [$params->paginate] default false, when true will automatically $paginate by calling this endpoint multiple times. See in the docs all the [available parameters](https://github.com/ccxt/ccxt/wiki/Manual#pagination-$params)
             * @return {int[][]} A list of candles ordered, open, high, low, close, volume
             */
            Async\await($this->load_markets());
            $paginate = false;
            list($paginate, $params) = $this->handle_option_and_params($params, 'fetchOHLCV', 'paginate', false);
            if ($paginate) {
                return Async\await($this->fetch_paginated_call_deterministic('fetchOHLCV', $symbol, $since, $limit, $timeframe, $params, 1440));
            }
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
            );
            $request['interval'] = $this->safe_string($this->timeframes, $timeframe, $timeframe);
            if ($since !== null) {
                $request['startTime'] = max ($since - 1, 0);
            }
            if ($limit !== null) {
                $request['limit'] = $limit;
            }
            $until = $this->safe_integer_2($params, 'until', 'endTime');
            if ($until !== null) {
                $params = $this->omit($params, array( 'until' ));
                $request['endTime'] = $until;
            }
            $response = null;
            if ($market['spot']) {
                $response = Async\await($this->spotV1PublicGetMarketKline ($this->extend($request, $params)));
            } else {
                if ($market['inverse']) {
                    $response = Async\await($this->cswapV1PublicGetMarketKlines ($this->extend($request, $params)));
                } else {
                    $price = $this->safe_string($params, 'price');
                    $params = $this->omit($params, 'price');
                    if ($price === 'mark') {
                        $response = Async\await($this->swapV1PublicGetMarketMarkPriceKlines ($this->extend($request, $params)));
                    } else {
                        $response = Async\await($this->swapV3PublicGetQuoteKlines ($this->extend($request, $params)));
                    }
                }
            }
            //
            //    {
            //        "code" => 0,
            //        "msg" => "",
            //        "data" => array(
            //          array(
            //            "open" => "19396.8",
            //            "close" => "19394.4",
