        //    {
        //        "time" => 1672025549368,
        //        "isBuyerMaker" => true,
        //        "price" => "16885.0",
        //        "qty" => "3.3002",
        //        "quoteQty" => "55723.87"
        //    }
        //
        // swap fetchMyTrades
        //
        //    {
        //        "volume" => "0.1",
        //        "price" => "106.75",
        //        "amount" => "10.6750",
        //        "commission" => "-0.0053",
        //        "currency" => "USDT",
        //        "orderId" => "1676213270274379776",
        //        "liquidatedPrice" => "0.00",
        //        "liquidatedMarginRatio" => "0.00",
        //        "filledTime" => "2023-07-04T20:56:01.000+0800"
        //    }
        //
        // ws spot
        //
        //    {
        //        "E" => 1690214529432,
        //        "T" => 1690214529386,
        //        "e" => "trade",
        //        "m" => true,
        //        "p" => "29110.19",
        //        "q" => "0.1868",
        //        "s" => "BTC-USDT",
        //        "t" => "57903921"
        //    }
        //
        // ws linear swap
        //
        //    {
        //        "q" => "0.0421",
        //        "p" => "29023.5",
        //        "T" => 1690221401344,
        //        "m" => false,
        //        "s" => "BTC-USDT"
        //    }
        //
        // ws inverse swap
        //
        //     {
        //         "e" => "trade",
        //         "E" => 1722920589665,
        //         "s" => "BTC-USD",
        //         "t" => "39125001",
        //         "p" => "55360.0",
        //         "q" => "1",
        //         "T" => 1722920589582,
        //         "m" => false
        //     }
        //
        // inverse swap fetchMyTrades
        //
        //     {
        //         "orderId" => "1817441228670648320",
        //         "symbol" => "SOL-USD",
        //         "type" => "MARKET",
        //         "side" => "BUY",
        //         "positionSide" => "LONG",
        //         "tradeId" => "97244554",
        //         "volume" => "2",
        //         "tradePrice" => "182.652",
        //         "amount" => "20.00000000",
        //         "realizedPnl" => "0.00000000",
        //         "commission" => "-0.00005475",
        //         "currency" => "SOL",
        //         "buyer" => true,
        //         "maker" => false,
        //         "tradeTime" => 1722146730000
        //     }
        //
        $time = $this->safe_integer_n($trade, array( 'time', 'filledTm', 'T', 'tradeTime' ));
        $datetimeId = $this->safe_string($trade, 'filledTm');
        if ($datetimeId !== null) {
            $time = $this->parse8601($datetimeId);
        }
        if ($time === 0) {
            $time = null;
        }
        $cost = $this->safe_string($trade, 'quoteQty');
        // $type = ($cost === null) ? 'spot' : 'swap'; this is not reliable
        $currencyId = $this->safe_string_n($trade, array( 'currency', 'N', 'commissionAsset' ));
        $currencyCode = $this->safe_currency_code($currencyId);
        $m = $this->safe_bool($trade, 'm');
        $marketId = $this->safe_string_2($trade, 's', 'symbol');
        $isBuyerMaker = $this->safe_bool_n($trade, array( 'buyerMaker', 'isBuyerMaker', 'maker' ));
        $takeOrMaker = null;
        if (($isBuyerMaker !== null) || ($m !== null)) {
            $takeOrMaker = ($isBuyerMaker || $m) ? 'maker' : 'taker';
        }
        $side = $this->safe_string_lower_2($trade, 'side', 'S');
        if ($side === null) {
            if (($isBuyerMaker !== null) || ($m !== null)) {
                $side = ($isBuyerMaker || $m) ? 'sell' : 'buy';
                $takeOrMaker = 'taker';
            }
        }
        $isBuyer = $this->safe_bool($trade, 'isBuyer');
        if ($isBuyer !== null) {
            $side = $isBuyer ? 'buy' : 'sell';
        }
        $isMaker = $this->safe_bool($trade, 'isMaker');
        if ($isMaker !== null) {
            $takeOrMaker = $isMaker ? 'maker' : 'taker';
        }
        $amount = $this->safe_string_n($trade, array( 'qty', 'amount', 'q' ));
        if (($market !== null) && $market['swap'] && (is_array($trade) && array_key_exists('volume', $trade))) {
            // private $trade returns num of contracts instead of base currency (as the order-related methods do)
            $contractSize = $this->safe_string($market['info'], 'tradeMinQuantity');
            $volume = $this->safe_string($trade, 'volume');
            $amount = Precise::string_mul($volume, $contractSize);
        }
        return $this->safe_trade(array(
            'id' => $this->safe_string_n($trade, array( 'id', 't' )),
            'info' => $trade,
            'timestamp' => $time,
            'datetime' => $this->iso8601($time),
            'symbol' => $this->safe_symbol($marketId, $market, '-'),
            'order' => $this->safe_string_2($trade, 'orderId', 'i'),
            'type' => $this->safe_string_lower($trade, 'o'),
            'side' => $this->parse_order_side($side),
            'takerOrMaker' => $takeOrMaker,
            'price' => $this->safe_string_n($trade, array( 'price', 'p', 'tradePrice' )),
            'amount' => $amount,
            'cost' => $cost,
            'fee' => array(
                'cost' => $this->parse_number(Precise::string_abs($this->safe_string_2($trade, 'commission', 'n'))),
                'currency' => $currencyCode,
            ),
        ), $market);
    }

