            //                 ["83780.1","18.0617"],
            //             ],
            //             "bidsCoin":[
            //                 ["83760.7","0.0007"],
            //                 ["83760.6","0.0000"],
            //                 ["83757.7","0.0002"],
            //             ],
            //             "asksCoin":[
            //                 ["83784.3","0.0008"],
            //                 ["83782.8","0.0024"],
            //                 ["83780.1","0.0018"],
