             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
            );
            $response = null;
            if ($market['inverse']) {
                $response = Async\await($this->cswapV1PublicGetMarketOpenInterest ($this->extend($request, $params)));
