                } else {
                    $response = Async\await($this->swapV2PublicGetQuoteTicker ($this->extend($request, $params)));
                }
            }
            //
            // spot and swap
            //
            //     {
            //         "code" => 0,
            //         "msg" => "",
            //         "timestamp" => 1720647285296,
            //         "data" => array(
            //             {
            //                 "symbol" => "SOL-USD",
