    public function parse_ticker(array $ticker, ?array $market = null): array {
        //
        // mark price
        // {
        //     "symbol" => "string",
        //     "lastFundingRate" => "string",
        //     "markPrice" => "string",
        //     "indexPrice" => "string",
        //     "nextFundingTime" => "int64"
        // }
        //
        // spot
        //    {
        //        "symbol" => "BTC-USDT",
        //        "openPrice" => "26032.08",
        //        "highPrice" => "26178.86",
        //        "lowPrice" => "25968.18",
        //        "lastPrice" => "26113.60",
        //        "volume" => "1161.79",
        //        "quoteVolume" => "30288466.44",
        //        "openTime" => "1693081020762",
        //        "closeTime" => "1693167420762",
        //  added 2023-11-10:
        //        "bidPrice" => 16726.0,
        //        "bidQty" => 0.05,
        //        "askPrice" => 16726.0,
        //        "askQty" => 0.05,
        //    }
        // swap
        //
        //    {
        //        "symbol" => "BTC-USDT",
        //        "priceChange" => "52.5",
        //        "priceChangePercent" => "0.31%", // they started to add the percent sign in value
        //        "lastPrice" => "16880.5",
        //        "lastQty" => "2.2238",          // only present in swap!
        //        "highPrice" => "16897.5",
        //        "lowPrice" => "16726.0",
        //        "volume" => "245870.1692",
        //        "quoteVolume" => "4151395117.73",
        //        "openPrice" => "16832.0",
        //        "openTime" => 1672026667803,
        //        "closeTime" => 1672026648425,
        //  added 2023-11-10:
        //        "bidPrice" => 16726.0,
        //        "bidQty" => 0.05,
        //        "askPrice" => 16726.0,
        //        "askQty" => 0.05,
        //    }
        //
        $marketId = $this->safe_string($ticker, 'symbol');
        $lastQty = $this->safe_string($ticker, 'lastQty');
        // in spot markets, $lastQty is not present
        // it's (bad, but) the only way we can check the tickers origin
        $type = ($lastQty === null) ? 'spot' : 'swap';
        $market = $this->safe_market($marketId, $market, null, $type);
        $symbol = $market['symbol'];
        $open = $this->safe_string($ticker, 'openPrice');
        $high = $this->safe_string($ticker, 'highPrice');
        $low = $this->safe_string($ticker, 'lowPrice');
        $close = $this->safe_string($ticker, 'lastPrice');
        $quoteVolume = $this->safe_string($ticker, 'quoteVolume');
        $baseVolume = $this->safe_string($ticker, 'volume');
        $percentage = $this->safe_string($ticker, 'priceChangePercent');
        if ($percentage !== null) {
            $percentage = str_replace('%', '', $percentage);
        }
        $change = $this->safe_string($ticker, 'priceChange');
        $ts = $this->safe_integer($ticker, 'closeTime');
        if ($ts === 0) {
            $ts = null;
        }
        $datetime = $this->iso8601($ts);
        $bid = $this->safe_string($ticker, 'bidPrice');
        $bidVolume = $this->safe_string($ticker, 'bidQty');
        $ask = $this->safe_string($ticker, 'askPrice');
        $askVolume = $this->safe_string($ticker, 'askQty');
        return $this->safe_ticker(array(
            'symbol' => $symbol,
            'timestamp' => $ts,
            'datetime' => $datetime,
            'high' => $high,
            'low' => $low,
            'bid' => $bid,
            'bidVolume' => $bidVolume,
            'ask' => $ask,
            'askVolume' => $askVolume,
            'vwap' => null,
            'open' => $open,
            'close' => $close,
            'last' => null,
            'previousClose' => null,
            'change' => $change,
            'percentage' => $percentage,
            'average' => null,
            'baseVolume' => $baseVolume,
            'quoteVolume' => $quoteVolume,
            'markPrice' => $this->safe_string($ticker, 'markPrice'),
            'indexPrice' => $this->safe_string($ticker, 'indexPrice'),
            'info' => $ticker,
        ), $market);
    }

