        // linear swap fetchPositionHistory
        //
        //     {
        //         "positionId" => "1861675561156571136",
        //         "symbol" => "LTC-USDT",
        //         "isolated" => false,
        //         "positionSide" => "LONG",
        //         "openTime" => 1732693017000,
