    public function parse_trade($trade, $market = null) {
        //
        // spot
        // fetchTrades
        //
        //    {
        //        "id" => 43148253,
        //        "price" => 25714.71,
        //        "qty" => 1.674571,
        //        "time" => 1655085975589,
        //        "buyerMaker" => false
        //    }
        //
        // swap
        // fetchTrades
        //
        //    {
        //        "time" => 1672025549368,
        //        "isBuyerMaker" => true,
        //        "price" => "16885.0",
        //        "qty" => "3.3002",
        //        "quoteQty" => "55723.87"
        //    }
        //
        // swap
        // fetchMyTrades
        //
        //    {
        //        volume => '0.1',
        //        price => '106.75',
        //        amount => '10.6750',
        //        commission => '-0.0053',
        //        currency => 'USDT',
        //        orderId => '1676213270274379776',
        //        liquidatedPrice => '0.00',
        //        liquidatedMarginRatio => '0.00',
        //        filledTime => '2023-07-04T20:56:01.000+0800'
        //    }
        //
        $time = $this->safe_integer_2($trade, 'time', 'filledTm');
        $datetimeId = $this->safe_string($trade, 'filledTm');
        if ($datetimeId !== null) {
            $time = $this->parse8601($datetimeId);
        }
        $isBuyerMaker = $this->safe_value_2($trade, 'buyerMaker', 'isBuyerMaker');
        $takeOrMaker = null;
        if ($isBuyerMaker) {
            $takeOrMaker = 'maker';
        } elseif ($isBuyerMaker !== null) {
            $takeOrMaker = 'taker';
        }
        $cost = $this->safe_string($trade, 'quoteQty');
        $type = ($cost === null) ? 'spot' : 'swap';
        $currencyId = $this->safe_string($trade, 'currency');
        $currencyCode = $this->safe_currency_code($currencyId);
        return $this->safe_trade(array(
            'id' => $this->safe_string_2($trade, 'id', 'orderId'),
            'info' => $trade,
            'timestamp' => $time,
            'datetime' => $this->iso8601($time),
            'symbol' => $this->safe_symbol(null, $market, '-', $type),
            'order' => null,
            'type' => null,
            'side' => null,
            'takerOrMaker' => $takeOrMaker,
            'price' => $this->safe_string($trade, 'price'),
            'amount' => $this->safe_string_2($trade, 'qty', 'amount'),
            'cost' => $cost,
            'fee' => array(
                'cost' => $this->parse_number(Precise::string_abs($this->safe_string($trade, 'commission'))),
                'currency' => $currencyCode,
                'rate' => null,
            ),
        ), $market);
    }

