    public function parse_balance($response) {
        $timestamp = null;
        $result = array(
            'info' => $response,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
        );
        $data = $this->safe_value($response, 'data', array());
        $keys = is_array($data) ? array_keys($data) : array();
        for ($i = 0; $i < count($keys); $i++) {
            $key = $keys[$i];
            $parts = explode('availableorder', $key);
            $numParts = count($parts);
            if ($numParts > 1) {
                $currencyId = $this->safe_string($parts, 1);
                // note that "Money" stands for INR - the only fiat in bitbns
                if ($currencyId === 'Money') {
                    $currencyId = 'INR';
                }
                $code = $this->safe_currency_code($currencyId);
                $account = $this->account();
                $account['free'] = $this->safe_string($data, $key);
                $account['used'] = $this->safe_string($data, 'inorder' . $currencyId);
                $result[$code] = $account;
            }
        }
        return $this->safe_balance($result);
    }

    public function fetch_balance($params = array ()) {
        return Async\async(function () use ($params) {
            /**
             * query for balance and get the amount of funds available for trading or funds locked in orders
             * @param {array} $params extra parameters specific to the bitbns api endpoint
             * @return {array} a ~@link https://docs.ccxt.com/en/latest/manual.html?#balance-structure balance structure~
             */
            Async\await($this->load_markets());
            $response = Async\await($this->v1PostCurrentCoinBalanceEVERYTHING ($params));
            //
            //     {
            //         "data":array(
            //             "availableorderMoney":12.34, // INR
            //             "availableorderBTC":0,
            //             "availableorderXRP":0,
            //             "inorderMoney":0, // INR
            //             "inorderBTC":0,
            //             "inorderXRP":0,
            //             "inorderNEO":0,
            //         ),
            //         "status":1,
            //         "error":null,
            //         "code":200
            //     }
            //
            // note that "Money" stands for INR - the only fiat in bitbns
            return $this->parse_balance($response);
        }) ();
    }

    public function parse_order_status($status) {
        $statuses = array(
            '0' => 'open',
            // 'PARTIALLY_FILLED' => 'open',
            // 'FILLED' => 'closed',
            // 'CANCELED' => 'canceled',
            // 'PENDING_CANCEL' => 'canceling', // currently unused
            // 'REJECTED' => 'rejected',
            // 'EXPIRED' => 'expired',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_order($order, $market = null) {
        //
        // createOrder
        //
        //     {
        //         "data":"Successfully placed bid to purchase currency",
        //         "status":1,
        //         "error":null,
        //         "id":5424475,
        //         "code":200
        //     }
        //
        // fetchOrder
        //
        //     {
        //         "entry_id":5424475,
        //         "btc":0.01,
        //         "rate":2000,
        //         "time":"2021-04-25T17:05:42.000Z",
        //         "type":0,
        //         "status":0,
        //         "total":0.01,
        //         "avg_cost":null,
        //         "side":"BUY",
        //         "amount":0.01,
        //         "remaining":0.01,
        //         "filled":0,
        //         "cost":null,
        //         "fee":0.05
        //     }
        //
        // fetchOpenOrders
        //
        //     {
        //         "entry_id":5424475,
        //         "btc":0.01,
        //         "rate":2000,
        //         "time":"2021-04-25T17:05:42.000Z",
        //         "type":0,
        //         "status":0
        //     }
        //
        $id = $this->safe_string_2($order, 'id', 'entry_id');
        $marketId = $this->safe_string($order, 'symbol');
        $symbol = $this->safe_symbol($marketId, $market);
        $timestamp = $this->parse8601($this->safe_string($order, 'time'));
        $price = $this->safe_string($order, 'rate');
        $amount = $this->safe_string_2($order, 'amount', 'btc');
        $filled = $this->safe_string($order, 'filled');
        $remaining = $this->safe_string($order, 'remaining');
        $average = $this->safe_string($order, 'avg_cost');
        $cost = $this->safe_string($order, 'cost');
        $type = $this->safe_string_lower($order, 'type');
        if ($type === '0') {
            $type = 'limit';
        }
        $status = $this->parse_order_status($this->safe_string($order, 'status'));
        $side = $this->safe_string_lower($order, 'side');
        $feeCost = $this->safe_number($order, 'fee');
        $fee = null;
        if ($feeCost !== null) {
            $feeCurrencyCode = null;
            $fee = array(
                'cost' => $feeCost,
                'currency' => $feeCurrencyCode,
            );
        }
        return $this->safe_order(array(
            'info' => $order,
            'id' => $id,
            'clientOrderId' => null,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'lastTradeTimestamp' => null,
            'symbol' => $symbol,
            'type' => $type,
            'timeInForce' => null,
            'postOnly' => null,
            'side' => $side,
            'price' => $price,
            'stopPrice' => null,
            'amount' => $amount,
            'cost' => $cost,
            'average' => $average,
            'filled' => $filled,
            'remaining' => $remaining,
            'status' => $status,
            'fee' => $fee,
            'trades' => null,
        ), $market);
    }

    public function create_order($symbol, $type, $side, $amount, $price = null, $params = array ()) {
        return Async\async(function () use ($symbol, $type, $side, $amount, $price, $params) {
            /**
             * create a trade order
             * @param {string} $symbol unified $symbol of the $market to create an order in
             * @param {string} $type 'market' or 'limit'
             * @param {string} $side 'buy' or 'sell'
             * @param {float} $amount how much of currency you want to trade in units of base currency
             * @param {float|null} $price the $price at which the order is to be fullfilled, in units of the quote currency, ignored in $market orders
             * @param {array} $params extra parameters specific to the bitbns api endpoint
             * @return {array} an {@link https://docs.ccxt.com/en/latest/manual.html#order-structure order structure}
             */
            if ($type !== 'limit' && $type !== 'market') {
                throw new ExchangeError($this->id . ' allows limit and $market orders only');
            }
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'side' => strtoupper($side),
                'symbol' => $market['uppercaseId'],
                'quantity' => $this->amount_to_precision($symbol, $amount),
                // 'target_rate' => $this->price_to_precision($symbol, targetRate),
                // 't_rate' => $this->price_to_precision($symbol, stopPrice),
                // 'trail_rate' => $this->price_to_precision($symbol, trailRate),
                // To Place Simple Buy or Sell Order use rate
                // To Place Stoploss Buy or Sell Order use rate & t_rate
                // To Place Bracket Buy or Sell Order use rate , t_rate, target_rate & trail_rate
            );
            $method = 'v2PostOrders';
            if ($type === 'limit') {
                $request['rate'] = $this->price_to_precision($symbol, $price);
            } elseif ($type === 'market') {
                $method = 'v1PostPlaceMarketOrderQntySymbol';
                $request['market'] = $market['quoteId'];
            } else {
                throw new ExchangeError($this->id . ' allows limit and $market orders only');
            }
            $response = Async\await($this->$method (array_merge($request, $params)));
            //
            //     {
            //         "data":"Successfully placed bid to purchase currency",
            //         "status":1,
            //         "error":null,
            //         "id":5424475,
            //         "code":200
            //     }
            //
            return $this->parse_order($response, $market);
        }) ();
    }

