    public function is_fiat($code) {
        return (is_array($this->options['fiat']) && array_key_exists($code, $this->options['fiat']));
    }

    public function get_currency_id($code) {
        return 'f' . $code;
    }

    public function get_currency_name($code) {
        // temporary fix for transpiler recognition, even though this is in parent class
        if (is_array($this->options['currencyNames']) && array_key_exists($code, $this->options['currencyNames'])) {
            return $this->options['currencyNames'][$code];
        }
        throw new NotSupported($this->id . ' ' . $code . ' not supported for withdrawal');
    }

    public function amount_to_precision($symbol, $amount) {
        // https://docs.bitfinex.com/docs/introduction#$amount-precision
        // The $amount field allows up to 8 decimals.
        // Anything exceeding this will be rounded to the 8th decimal.
        $symbol = $this->safe_symbol($symbol);
        return $this->decimal_to_precision($amount, TRUNCATE, $this->markets[$symbol]['precision']['amount'], DECIMAL_PLACES);
    }

    public function price_to_precision($symbol, $price) {
        $symbol = $this->safe_symbol($symbol);
        $price = $this->decimal_to_precision($price, ROUND, $this->markets[$symbol]['precision']['price'], $this->precisionMode);
        // https://docs.bitfinex.com/docs/introduction#$price-precision
        // The precision level of all trading prices is based on significant figures.
        // All pairs on Bitfinex use up to 5 significant digits and up to 8 decimals (e.g. 1.2345, 123.45, 1234.5, 0.00012345).
        // Prices submit with a precision larger than 5 will be cut by the API.
        return $this->decimal_to_precision($price, TRUNCATE, 8, DECIMAL_PLACES);
    }

    public function fetch_status($params = array ()) {
        return Async\async(function () use ($params) {
            /**
             * the latest known information on the availability of the exchange API
             * @see https://docs.bitfinex.com/reference/rest-public-platform-status
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=exchange-status-structure status structure~
             */
            //
            //    [1] // operative
            //    [0] // maintenance
            //
            $response = Async\await($this->publicGetPlatformStatus ($params));
            $statusRaw = $this->safe_string($response, 0);
            return array(
                'status' => $this->safe_string(array( '0' => 'maintenance', '1' => 'ok' ), $statusRaw, $statusRaw),
                'updated' => null,
                'eta' => null,
                'url' => null,
                'info' => $response,
            );
        }) ();
    }

    public function fetch_markets($params = array ()): PromiseInterface {
        return Async\async(function () use ($params) {
            /**
             * retrieves data on all $markets for bitfinex2
             * @see https://docs.bitfinex.com/reference/rest-public-conf
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array[]} an array of objects representing $market data
             */
            $spotMarketsInfoPromise = $this->publicGetConfPubInfoPair ($params);
            $futuresMarketsInfoPromise = $this->publicGetConfPubInfoPairFutures ($params);
            $marginIdsPromise = $this->publicGetConfPubListPairMargin ($params);
            list($spotMarketsInfo, $futuresMarketsInfo, $marginIds) = Async\await(Promise\all(array( $spotMarketsInfoPromise, $futuresMarketsInfoPromise, $marginIdsPromise )));
            $spotMarketsInfo = $this->safe_list($spotMarketsInfo, 0, array());
            $futuresMarketsInfo = $this->safe_list($futuresMarketsInfo, 0, array());
            $markets = $this->array_concat($spotMarketsInfo, $futuresMarketsInfo);
            $marginIds = $this->safe_value($marginIds, 0, array());
            //
            //    array(
            //        "1INCH:USD",
            //        array(
            //           null,
            //           null,
            //           null,
            //           "2.0",
            //           "100000.0",
            //           null,
            //           null,
            //           null,
            //           null,
            //           null,
            //           null,
            //           null
            //        )
            //    )
            //
            $result = array();
            for ($i = 0; $i < count($markets); $i++) {
                $pair = $markets[$i];
                $id = $this->safe_string_upper($pair, 0);
                $market = $this->safe_value($pair, 1, array());
                $spot = true;
                if (mb_strpos($id, 'F0') !== false) {
                    $spot = false;
                }
                $swap = !$spot;
                $baseId = null;
                $quoteId = null;
                if (mb_strpos($id, ':') !== false) {
                    $parts = explode(':', $id);
                    $baseId = $parts[0];
                    $quoteId = $parts[1];
                } else {
                    $baseId = mb_substr($id, 0, 3 - 0);
                    $quoteId = mb_substr($id, 3, 6 - 3);
                }
                $base = $this->safe_currency_code($baseId);
                $quote = $this->safe_currency_code($quoteId);
                $splitBase = explode('F0', $base);
                $splitQuote = explode('F0', $quote);
                $base = $this->safe_string($splitBase, 0);
                $quote = $this->safe_string($splitQuote, 0);
                $symbol = $base . '/' . $quote;
                $baseId = $this->get_currency_id($baseId);
                $quoteId = $this->get_currency_id($quoteId);
                $settle = null;
                $settleId = null;
                if ($swap) {
                    $settle = $quote;
                    $settleId = $quote;
                    $symbol = $symbol . ':' . $settle;
                }
                $minOrderSizeString = $this->safe_string($market, 3);
                $maxOrderSizeString = $this->safe_string($market, 4);
                $margin = false;
                if ($spot && $this->in_array($id, $marginIds)) {
                    $margin = true;
                }
                $result[] = array(
                    'id' => 't' . $id,
                    'symbol' => $symbol,
                    'base' => $base,
                    'quote' => $quote,
                    'settle' => $settle,
                    'baseId' => $baseId,
                    'quoteId' => $quoteId,
                    'settleId' => $settleId,
                    'type' => $spot ? 'spot' : 'swap',
                    'spot' => $spot,
                    'margin' => $margin,
                    'swap' => $swap,
                    'future' => false,
                    'option' => false,
                    'active' => true,
                    'contract' => $swap,
                    'linear' => $swap ? true : null,
                    'inverse' => $swap ? false : null,
                    'contractSize' => $swap ? $this->parse_number('1') : null,
                    'expiry' => null,
                    'expiryDatetime' => null,
                    'strike' => null,
                    'optionType' => null,
                    'precision' => array(
                        'amount' => intval('8'), // https://github.com/ccxt/ccxt/issues/7310
                        'price' => intval('5'),
                    ),
                    'limits' => array(
                        'leverage' => array(
                            'min' => null,
                            'max' => null,
                        ),
                        'amount' => array(
                            'min' => $this->parse_number($minOrderSizeString),
                            'max' => $this->parse_number($maxOrderSizeString),
                        ),
                        'price' => array(
                            'min' => $this->parse_number('1e-8'),
                            'max' => null,
                        ),
                        'cost' => array(
                            'min' => null,
                            'max' => null,
                        ),
                    ),
                    'created' => null, // todo => the api needs revision for extra $params & endpoints for possibility of returning a timestamp for this
                    'info' => $market,
                );
            }
            return $result;
        }) ();
    }

    public function fetch_currencies($params = array ()): PromiseInterface {
        return Async\async(function () use ($params) {
            /**
             * fetches all available currencies on an exchange
             * @see https://docs.bitfinex.com/reference/rest-public-conf
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} an associative dictionary of currencies
             */
            $labels = array(
                'pub:list:currency',
                'pub:map:currency:sym', // maps symbols to their API symbols, BAB > BCH
                'pub:map:currency:label', // verbose friendly names, BNT > Bancor
                'pub:map:currency:unit', // maps symbols to unit of measure where applicable
                'pub:map:currency:undl', // maps derivatives symbols to their underlying currency
                'pub:map:currency:pool', // maps symbols to underlying network/protocol they operate on
                'pub:map:currency:explorer', // maps symbols to their recognised block explorer URLs
                'pub:map:currency:tx:fee', // maps currencies to their withdrawal $fees https://github.com/ccxt/ccxt/issues/7745,
                'pub:map:tx:method', // maps withdrawal/deposit methods to their API symbols
            );
            $config = implode(',', $labels);
            $request = array(
                'config' => $config,
            );
            $response = Async\await($this->publicGetConfConfig ($this->extend($request, $params)));
            //
            //     [
            //
            //         a list of symbols
            //         ["AAA","ABS","ADA"],
            //
            //         // sym
            //         // maps symbols to their API symbols, BAB > BCH
            //         array(
            //             array( "BAB", "BCH" ),
            //             array( "CNHT", "CNHt" ),
            //             array( "DSH", "DASH" ),
            //             array( "IOT", "IOTA" ),
            //             array( "LES", "LEO-EOS" ),
            //             array( "LET", "LEO-ERC20" ),
            //             array( "STJ", "STORJ" ),
            //             array( "TSD", "TUSD" ),
            //             array( "UDC", "USDC" ),
            //             array( "USK", "USDK" ),
            //             array( "UST", "USDt" ),
            //             array( "USTF0", "USDt0" ),
            //             array( "XCH", "XCHF" ),
            //             array( "YYW", "YOYOW" ),
            //             // ...
            //         ),
            //         // $label
            //         // verbose friendly names, BNT > Bancor
            //         array(
            //             array( "BAB", "Bitcoin Cash" ),
            //             array( "BCH", "Bitcoin Cash" ),
            //             array( "LEO", "Unus Sed LEO" ),
            //             array( "LES", "Unus Sed LEO (EOS)" ),
            //             array( "LET", "Unus Sed LEO (ERC20)" ),
            //             // ...
            //         ),
            //         // unit
            //         // maps symbols to unit of measure where applicable
            //         array(
            //             array( "IOT", "Mi|MegaIOTA" ),
            //         ),
            //         // $undl
            //         // maps derivatives symbols to their underlying currency
            //         array(
            //             array( "USTF0", "UST" ),
            //             array( "BTCF0", "BTC" ),
            //             array( "ETHF0", "ETH" ),
            //         ),
            //         // $pool
            //         // maps symbols to underlying network/protocol they operate on
            //         array(
            //             array( 'SAN', 'ETH' ), array( 'OMG', 'ETH' ), array( 'AVT', 'ETH' ), array( "EDO", "ETH" ),
            //             array( 'ESS', 'ETH' ), array( 'ATD', 'EOS' ), array( 'ADD', 'EOS' ), array( "MTO", "EOS" ),
            //             array( 'PNK', 'ETH' ), array( 'BAB', 'BCH' ), array( 'WLO', 'XLM' ), array( "VLD", "ETH" ),
            //             array( 'BTT', 'TRX' ), array( 'IMP', 'ETH' ), array( 'SCR', 'ETH' ), array( "GNO", "ETH" ),
            //             // ...
            //         ),
            //         // explorer
            //         // maps symbols to their recognised block explorer URLs
            //         array(
            //             array(
            //                 "AIO",
            //                 array(
            //                     "https://mainnet.aion.network",
            //                     "https://mainnet.aion.network/#/account/VAL",
            //                     "https://mainnet.aion.network/#/transaction/VAL"
            //                 )
            //             ),
            //             // ...
            //         ),
            //         // $fee
            //         // maps currencies to their withdrawal $fees
            //         [
            //             ["AAA",[0,0]],
            //             ["ABS",[0,131.3]],
            //             ["ADA",[0,0.3]],
            //         ],
            //     ]
            //
            $indexed = array(
                'sym' => $this->index_by($this->safe_value($response, 1, array()), 0),
                'label' => $this->index_by($this->safe_value($response, 2, array()), 0),
                'unit' => $this->index_by($this->safe_value($response, 3, array()), 0),
                'undl' => $this->index_by($this->safe_value($response, 4, array()), 0),
                'pool' => $this->index_by($this->safe_value($response, 5, array()), 0),
                'explorer' => $this->index_by($this->safe_value($response, 6, array()), 0),
                'fees' => $this->index_by($this->safe_value($response, 7, array()), 0),
            );
            $ids = $this->safe_value($response, 0, array());
            $result = array();
            for ($i = 0; $i < count($ids); $i++) {
                $id = $ids[$i];
                if (mb_strpos($id, 'F0') !== false) {
                    // we get a lot of F0 currencies, skip those
                    continue;
                }
                $code = $this->safe_currency_code($id);
                $label = $this->safe_value($indexed['label'], $id, array());
                $name = $this->safe_string($label, 1);
                $pool = $this->safe_value($indexed['pool'], $id, array());
                $rawType = $this->safe_string($pool, 1);
                $type = ($rawType === null) ? 'other' : 'crypto';
                $feeValues = $this->safe_value($indexed['fees'], $id, array());
                $fees = $this->safe_value($feeValues, 1, array());
                $fee = $this->safe_number($fees, 1);
                $undl = $this->safe_value($indexed['undl'], $id, array());
                $precision = '8'; // default $precision, todo => fix "magic constants"
                $fid = 'f' . $id;
                $result[$code] = array(
                    'id' => $fid,
                    'uppercaseId' => $id,
                    'code' => $code,
                    'info' => array( $id, $label, $pool, $feeValues, $undl ),
                    'type' => $type,
                    'name' => $name,
                    'active' => true,
                    'deposit' => null,
                    'withdraw' => null,
                    'fee' => $fee,
                    'precision' => intval($precision),
                    'limits' => array(
                        'amount' => array(
                            'min' => $this->parse_number($this->parse_precision($precision)),
                            'max' => null,
                        ),
                        'withdraw' => array(
                            'min' => $fee,
                            'max' => null,
                        ),
                    ),
                    'networks' => array(),
                );
                $networks = array();
                $currencyNetworks = $this->safe_value($response, 8, array());
                $cleanId = str_replace('F0', '', $id);
                for ($j = 0; $j < count($currencyNetworks); $j++) {
                    $pair = $currencyNetworks[$j];
                    $networkId = $this->safe_string($pair, 0);
                    $currencyId = $this->safe_string($this->safe_value($pair, 1, array()), 0);
                    if ($currencyId === $cleanId) {
                        $network = $this->network_id_to_code($networkId);
                        $networks[$network] = array(
                            'info' => $networkId,
                            'id' => strtolower($networkId),
                            'network' => $networkId,
                            'active' => null,
                            'deposit' => null,
                            'withdraw' => null,
                            'fee' => null,
                            'precision' => null,
                            'limits' => array(
                                'withdraw' => array(
                                    'min' => null,
                                    'max' => null,
                                ),
                            ),
                        );
                    }
                }
                $keysNetworks = is_array($networks) ? array_keys($networks) : array();
                $networksLength = count($keysNetworks);
                if ($networksLength > 0) {
                    $result[$code]['networks'] = $networks;
                }
            }
            return $result;
        }) ();
    }

    public function fetch_balance($params = array ()): PromiseInterface {
        return Async\async(function () use ($params) {
            /**
             * $query for $balance and get the amount of funds available for trading or funds locked in orders
             * @see https://docs.bitfinex.com/reference/rest-auth-wallets
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=$balance-structure $balance structure~
             */
            // this api call does not return the 'used' amount - use the v1 version instead (which also returns zero balances)
            // there is a difference between this and the v1 api, namely trading wallet is called margin in v2
            Async\await($this->load_markets());
            $accountsByType = $this->safe_value($this->options, 'v2AccountsByType', array());
            $requestedType = $this->safe_string($params, 'type', 'exchange');
            $accountType = $this->safe_string($accountsByType, $requestedType, $requestedType);
            if ($accountType === null) {
                $keys = is_array($accountsByType) ? array_keys($accountsByType) : array();
                throw new ExchangeError($this->id . ' fetchBalance() $type parameter must be one of ' . implode(', ', $keys));
            }
            $isDerivative = $requestedType === 'derivatives';
            $query = $this->omit($params, 'type');
            $response = Async\await($this->privatePostAuthRWallets ($query));
            $result = array( 'info' => $response );
            for ($i = 0; $i < count($response); $i++) {
                $balance = $response[$i];
                $account = $this->account();
                $interest = $this->safe_string($balance, 3);
                if ($interest !== '0') {
                    $account['debt'] = $interest;
                }
                $type = $this->safe_string($balance, 0);
                $currencyId = $this->safe_string_lower($balance, 1, '');
                $start = strlen($currencyId) - 2;
                $isDerivativeCode = mb_substr($currencyId, $start) === 'f0';
                // this will only filter the derivative codes if the $requestedType is 'derivatives'
                $derivativeCondition = (!$isDerivative || $isDerivativeCode);
                if (($accountType === $type) && $derivativeCondition) {
                    $code = $this->safe_currency_code($currencyId);
                    $account['total'] = $this->safe_string($balance, 2);
                    $account['free'] = $this->safe_string($balance, 4);
                    $result[$code] = $account;
                }
            }
            return $this->safe_balance($result);
        }) ();
    }

    public function transfer(string $code, float $amount, string $fromAccount, string $toAccount, $params = array ()): PromiseInterface {
        return Async\async(function () use ($code, $amount, $fromAccount, $toAccount, $params) {
            /**
             * transfer $currency internally between wallets on the same account
             * @see https://docs.bitfinex.com/reference/rest-auth-transfer
             * @param {string} $code unified $currency $code
             * @param {float} $amount amount to transfer
             * @param {string} $fromAccount account to transfer from
             * @param {string} $toAccount account to transfer to
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=transfer-structure transfer structure~
             */
            // transferring between derivatives wallet and regular wallet is not documented in their API
            // however we support it in CCXT (from just looking at web inspector)
            Async\await($this->load_markets());
            $accountsByType = $this->safe_value($this->options, 'v2AccountsByType', array());
            $fromId = $this->safe_string($accountsByType, $fromAccount);
            if ($fromId === null) {
                $keys = is_array($accountsByType) ? array_keys($accountsByType) : array();
                throw new ArgumentsRequired($this->id . ' transfer() $fromAccount must be one of ' . implode(', ', $keys));
            }
            $toId = $this->safe_string($accountsByType, $toAccount);
            if ($toId === null) {
                $keys = is_array($accountsByType) ? array_keys($accountsByType) : array();
                throw new ArgumentsRequired($this->id . ' transfer() $toAccount must be one of ' . implode(', ', $keys));
            }
            $currency = $this->currency($code);
            $fromCurrencyId = $this->convert_derivatives_id($currency, $fromAccount);
            $toCurrencyId = $this->convert_derivatives_id($currency, $toAccount);
            $requestedAmount = $this->currency_to_precision($code, $amount);
            // this $request is slightly different from v1 $fromAccount -> from
            $request = array(
                'amount' => $requestedAmount,
                'currency' => $fromCurrencyId,
                'currency_to' => $toCurrencyId,
                'from' => $fromId,
                'to' => $toId,
            );
            $response = Async\await($this->privatePostAuthWTransfer ($this->extend($request, $params)));
            //
            //     array(
            //         1616451183763,
            //         "acc_tf",
            //         null,
            //         null,
            //         array(
            //             1616451183763,
            //             "exchange",
            //             "margin",
            //             null,
            //             "UST",
            //             "UST",
            //             null,
            //             1
            //         ),
            //         null,
            //         "SUCCESS",
            //         "1.0 Tether USDt transfered from Exchange to Margin"
            //     )
            //
            $error = $this->safe_string($response, 0);
            if ($error === 'error') {
                $message = $this->safe_string($response, 2, '');
                // same $message v1
                $this->throw_exactly_matched_exception($this->exceptions['exact'], $message, $this->id . ' ' . $message);
                throw new ExchangeError($this->id . ' ' . $message);
            }
            return $this->parse_transfer(array( 'result' => $response ), $currency);
        }) ();
    }

    public function parse_transfer(array $transfer, ?array $currency = null): array {
        //
        // $transfer
        //
        //     array(
        //         1616451183763,
        //         "acc_tf",
        //         null,
        //         null,
        //         array(
        //             1616451183763,
        //             "exchange",
        //             "margin",
        //             null,
        //             "UST",
        //             "UST",
        //             null,
        //             1
        //         ),
        //         null,
        //         "SUCCESS",
        //         "1.0 Tether USDt transfered from Exchange to Margin"
        //     )
        //
        $result = $this->safe_list($transfer, 'result');
        $timestamp = $this->safe_integer($result, 0);
        $info = $this->safe_value($result, 4);
        $fromAccount = $this->safe_string($info, 1);
        $toAccount = $this->safe_string($info, 2);
        $currencyId = $this->safe_string($info, 5);
        $status = $this->safe_string($result, 6);
        return array(
            'id' => null,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'status' => $this->parse_transfer_status($status),
            'amount' => $this->safe_number($info, 7),
            'currency' => $this->safe_currency_code($currencyId, $currency),
            'fromAccount' => $fromAccount,
            'toAccount' => $toAccount,
            'info' => $result,
        );
    }

    public function parse_transfer_status(?string $status): ?string {
        $statuses = array(
            'SUCCESS' => 'ok',
            'ERROR' => 'failed',
            'FAILURE' => 'failed',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function convert_derivatives_id($currency, $type) {
        // there is a difference between this and the v1 api, namely trading wallet is called margin in v2
        // {
        //   "id" => "fUSTF0",
        //   "code" => "USTF0",
        //   "info" => array( 'USTF0', array(), array(), array(), array( "USTF0", "UST" ) ),
        $info = $this->safe_value($currency, 'info');
        $transferId = $this->safe_string($info, 0);
        $underlying = $this->safe_value($info, 4, array());
        $currencyId = null;
        if ($type === 'derivatives') {
            $currencyId = $this->safe_string($underlying, 0, $transferId);
            $start = strlen($currencyId) - 2;
            $isDerivativeCode = mb_substr($currencyId, $start) === 'F0';
            if (!$isDerivativeCode) {
                $currencyId = $currencyId . 'F0';
            }
        } elseif ($type !== 'margin') {
            $currencyId = $this->safe_string($underlying, 1, $transferId);
        } else {
            $currencyId = $transferId;
        }
        return $currencyId;
    }

    public function fetch_order_book(string $symbol, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $limit, $params) {
            /**
             * fetches information on open orders with bid (buy) and ask (sell) prices, volumes and other data
             * @see https://docs.bitfinex.com/reference/rest-public-book
             * @param {string} $symbol unified $symbol of the $market to fetch the $order book for
             * @param {int} [$limit] the maximum $amount of $order book entries to return, bitfinex only allows 1, 25, or 100
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} A dictionary of ~@link https://docs.ccxt.com/#/?id=$order-book-structure $order book structures~ indexed by $market symbols
             */
            Async\await($this->load_markets());
            $precision = $this->safe_value($this->options, 'precision', 'R0');
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
                'precision' => $precision,
            );
            if ($limit !== null) {
                $request['len'] = $limit;
            }
            $fullRequest = $this->extend($request, $params);
            $orderbook = Async\await($this->publicGetBookSymbolPrecision ($fullRequest));
            $timestamp = $this->milliseconds();
            $result = array(
                'symbol' => $market['symbol'],
                'bids' => array(),
                'asks' => array(),
                'timestamp' => $timestamp,
                'datetime' => $this->iso8601($timestamp),
                'nonce' => null,
            );
            $priceIndex = ($fullRequest['precision'] === 'R0') ? 1 : 0;
            for ($i = 0; $i < count($orderbook); $i++) {
                $order = $orderbook[$i];
                $price = $this->safe_number($order, $priceIndex);
                $signedAmount = $this->safe_string($order, 2);
                $amount = Precise::string_abs($signedAmount);
                $side = Precise::string_gt($signedAmount, '0') ? 'bids' : 'asks';
                $result[$side][] = array( $price, $this->parse_number($amount) );
            }
            $result['bids'] = $this->sort_by($result['bids'], 0, true);
            $result['asks'] = $this->sort_by($result['asks'], 0);
            return $result;
        }) ();
    }

    public function parse_ticker(array $ticker, ?array $market = null): array {
        //
        // on trading pairs (ex. tBTCUSD)
        //
        //    {
        //        'result' => array(
        //            SYMBOL,
        //            BID,
        //            BID_SIZE,
        //            ASK,
        //            ASK_SIZE,
        //            DAILY_CHANGE,
        //            DAILY_CHANGE_RELATIVE,
        //            LAST_PRICE,
        //            VOLUME,
        //            HIGH,
        //            LOW
        //        )
        //    }
        //
        //
        // on funding currencies (ex. fUSD)
        //
        //    {
        //        'result' => array(
        //            SYMBOL,
        //            FRR,
        //            BID,
        //            BID_PERIOD,
        //            BID_SIZE,
        //            ASK,
        //            ASK_PERIOD,
        //            ASK_SIZE,
        //            DAILY_CHANGE,
        //            DAILY_CHANGE_RELATIVE,
        //            LAST_PRICE,
        //            VOLUME,
        //            HIGH,
        //            LOW,
        //            _PLACEHOLDER,
        //            _PLACEHOLDER,
        //            FRR_AMOUNT_AVAILABLE
        //        )
        //    }
        //
        $result = $this->safe_list($ticker, 'result');
        $symbol = $this->safe_symbol(null, $market);
        $length = count($result);
        $last = $this->safe_string($result, $length - 4);
        $percentage = $this->safe_string($result, $length - 5);
        return $this->safe_ticker(array(
            'symbol' => $symbol,
            'timestamp' => null,
            'datetime' => null,
            'high' => $this->safe_string($result, $length - 2),
            'low' => $this->safe_string($result, $length - 1),
            'bid' => $this->safe_string($result, $length - 10),
            'bidVolume' => $this->safe_string($result, $length - 9),
            'ask' => $this->safe_string($result, $length - 8),
            'askVolume' => $this->safe_string($result, $length - 7),
            'vwap' => null,
            'open' => null,
            'close' => $last,
            'last' => $last,
            'previousClose' => null,
            'change' => $this->safe_string($result, $length - 6),
            'percentage' => Precise::string_mul($percentage, '100'),
            'average' => null,
            'baseVolume' => $this->safe_string($result, $length - 3),
            'quoteVolume' => null,
            'info' => $result,
        ), $market);
    }

    public function fetch_tickers(?array $symbols = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbols, $params) {
            /**
             * fetches price $tickers for multiple markets, statistical information calculated over the past 24 hours for each $market
             * @see https://docs.bitfinex.com/reference/rest-public-$tickers
             * @param {string[]|null} $symbols unified $symbols of the markets to fetch the $ticker for, all $market $tickers are returned if not assigned
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} a dictionary of ~@link https://docs.ccxt.com/#/?id=$ticker-structure $ticker structures~
             */
            Async\await($this->load_markets());
            $symbols = $this->market_symbols($symbols);
            $request = array();
            if ($symbols !== null) {
                $ids = $this->market_ids($symbols);
                $request['symbols'] = implode(',', $ids);
            } else {
                $request['symbols'] = 'ALL';
            }
            $tickers = Async\await($this->publicGetTickers ($this->extend($request, $params)));
            //
            //     array(
            //         // on trading pairs (ex. tBTCUSD)
            //         array(
            //             SYMBOL,
            //             BID,
            //             BID_SIZE,
            //             ASK,
            //             ASK_SIZE,
            //             DAILY_CHANGE,
            //             DAILY_CHANGE_RELATIVE,
            //             LAST_PRICE,
            //             VOLUME,
            //             HIGH,
            //             LOW
            //         ),
            //         // on funding currencies (ex. fUSD)
            //         array(
            //             SYMBOL,
            //             FRR,
            //             BID,
            //             BID_PERIOD,
            //             BID_SIZE,
            //             ASK,
            //             ASK_PERIOD,
            //             ASK_SIZE,
            //             DAILY_CHANGE,
            //             DAILY_CHANGE_RELATIVE,
            //             LAST_PRICE,
            //             VOLUME,
            //             HIGH,
            //             LOW,
            //             _PLACEHOLDER,
            //             _PLACEHOLDER,
            //             FRR_AMOUNT_AVAILABLE
            //         ),
            //         ...
            //     )
            //
            $result = array();
            for ($i = 0; $i < count($tickers); $i++) {
                $ticker = $tickers[$i];
                $marketId = $this->safe_string($ticker, 0);
                $market = $this->safe_market($marketId);
                $symbol = $market['symbol'];
                $result[$symbol] = $this->parse_ticker(array( 'result' => $ticker ), $market);
            }
            return $this->filter_by_array_tickers($result, 'symbol', $symbols);
        }) ();
    }

    public function fetch_ticker(string $symbol, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $params) {
            /**
             * fetches a price $ticker, a statistical calculation with the information calculated over the past 24 hours for a specific $market
             * @see https://docs.bitfinex.com/reference/rest-public-$ticker
             * @param {string} $symbol unified $symbol of the $market to fetch the $ticker for
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=$ticker-structure $ticker structure~
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
            );
            $ticker = Async\await($this->publicGetTickerSymbol ($this->extend($request, $params)));
            $result = array( 'result' => $ticker );
            return $this->parse_ticker($result, $market);
        }) ();
    }

    public function parse_trade(array $trade, ?array $market = null): array {
        //
        // fetchTrades (public)
        //
        //     array(
        //         ID,
        //         MTS, // $timestamp
        //         AMOUNT,
        //         PRICE
        //     )
        //
        // fetchMyTrades (private)
        //
        //     array(
        //         ID,
        //         PAIR,
        //         MTS_CREATE,
        //         ORDER_ID,
        //         EXEC_AMOUNT,
        //         EXEC_PRICE,
        //         ORDER_TYPE,
        //         ORDER_PRICE,
        //         MAKER,
        //         FEE,
        //         FEE_CURRENCY,
        //         ...
        //     )
        //
        $tradeList = $this->safe_list($trade, 'result', array());
        $tradeLength = count($tradeList);
        $isPrivate = ($tradeLength > 5);
        $id = $this->safe_string($tradeList, 0);
        $amountIndex = $isPrivate ? 4 : 2;
        $side = null;
        $amountString = $this->safe_string($tradeList, $amountIndex);
        $priceIndex = $isPrivate ? 5 : 3;
        $priceString = $this->safe_string($tradeList, $priceIndex);
        if ($amountString[0] === '-') {
            $side = 'sell';
            $amountString = Precise::string_abs($amountString);
        } else {
            $side = 'buy';
        }
        $orderId = null;
        $takerOrMaker = null;
        $type = null;
        $fee = null;
        $symbol = $this->safe_symbol(null, $market);
        $timestampIndex = $isPrivate ? 2 : 1;
        $timestamp = $this->safe_integer($tradeList, $timestampIndex);
        if ($isPrivate) {
            $marketId = $tradeList[1];
            $symbol = $this->safe_symbol($marketId);
            $orderId = $this->safe_string($tradeList, 3);
            $maker = $this->safe_integer($tradeList, 8);
            $takerOrMaker = ($maker === 1) ? 'maker' : 'taker';
            $feeCostString = $this->safe_string($tradeList, 9);
            $feeCostString = Precise::string_neg($feeCostString);
            $feeCurrencyId = $this->safe_string($tradeList, 10);
            $feeCurrency = $this->safe_currency_code($feeCurrencyId);
            $fee = array(
                'cost' => $feeCostString,
                'currency' => $feeCurrency,
            );
            $orderType = $tradeList[6];
            $type = $this->safe_string($this->options['exchangeTypes'], $orderType);
        }
        return $this->safe_trade(array(
            'id' => $id,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'order' => $orderId,
            'side' => $side,
            'type' => $type,
            'takerOrMaker' => $takerOrMaker,
            'price' => $priceString,
            'amount' => $amountString,
            'cost' => null,
            'fee' => $fee,
            'info' => $tradeList,
        ), $market);
    }

    public function fetch_trades(string $symbol, ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * get the list of most recent $trades for a particular $symbol
             * @see https://docs.bitfinex.com/reference/rest-public-$trades
             * @param {string} $symbol unified $symbol of the $market to fetch $trades for
             * @param {int} [$since] timestamp in ms of the earliest trade to fetch
             * @param {int} [$limit] the maximum amount of $trades to fetch, default 120, max 10000
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @param {boolean} [$params->paginate] default false, when true will automatically $paginate by calling this endpoint multiple times. See in the docs all the [available parameters](https://github.com/ccxt/ccxt/wiki/Manual#pagination-$params)
             * @param {int} [$params->until] the latest time in ms to fetch entries for
             * @return {Trade[]} a list of ~@link https://docs.ccxt.com/#/?id=public-$trades trade structures~
             */
            Async\await($this->load_markets());
            $paginate = false;
            list($paginate, $params) = $this->handle_option_and_params($params, 'fetchTrades', 'paginate');
            if ($paginate) {
                return Async\await($this->fetch_paginated_call_dynamic('fetchTrades', $symbol, $since, $limit, $params, 10000));
            }
            $market = $this->market($symbol);
            $sort = '-1';
            $request = array(
                'symbol' => $market['id'],
            );
            if ($since !== null) {
                $request['start'] = $since;
                $sort = '1';
            }
            if ($limit !== null) {
                $request['limit'] = min ($limit, 10000); // default 120, max 10000
            }
            $request['sort'] = $sort;
            list($request, $params) = $this->handle_until_option('end', $request, $params);
            $response = Async\await($this->publicGetTradesSymbolHist ($this->extend($request, $params)));
            //
            //     array(
            //         array(
            //             ID,
            //             MTS, // timestamp
            //             AMOUNT,
            //             PRICE
            //         )
            //     )
            //
            $trades = $this->sort_by($response, 1);
            $tradesList = array();
            for ($i = 0; $i < count($trades); $i++) {
                $tradesList[] = array( 'result' => $trades[$i] ); // convert to array of dicts to match parseOrder signature
            }
            return $this->parse_trades($tradesList, $market, null, $limit);
        }) ();
    }

    public function fetch_ohlcv(string $symbol, $timeframe = '1m', ?int $since = null, ?int $limit = 100, $params = array ()): PromiseInterface {
