    public function fetch_currencies($params = array ()) {
        return Async\async(function () use ($params) {
            /**
             * fetches all available currencies on an exchange
             * @see https://bitgetlimited.github.io/apidoc/en/spot/#get-coin-list
             * @param {array} [$params] extra parameters specific to the bitget api endpoint
             * @return {array} an associative dictionary of currencies
             */
            $response = Async\await($this->publicSpotGetPublicCurrencies ($params));
            //
            //     {
            //       $code => '00000',
            //       msg => 'success',
            //       requestTime => 1645935668288,
            //       $data => array(
            //         {
            //           coinId => '230',
            //           coinName => 'KIN',
            //           transfer => 'false',
            //           $chains => array(
            //             {
            //               $chain => 'SOL',
            //               needTag => 'false',
            //               withdrawable => 'true',
            //               rechargeable => 'true',
            //               withdrawFee => '187500',
            //               depositConfirm => '100',
            //               withdrawConfirm => '100',
            //               minDepositAmount => '12500',
            //               minWithdrawAmount => '250000',
            //               browserUrl => 'https://explorer.solana.com/tx/'
            //             }
            //           )
            //         }
            //       )
            //     }
            //
            $result = array();
            $data = $this->safe_value($response, 'data', array());
            for ($i = 0; $i < count($data); $i++) {
                $entry = $data[$i];
                $id = $this->safe_string($entry, 'coinId');
                $code = $this->safe_currency_code($this->safe_string($entry, 'coinName'));
                $chains = $this->safe_value($entry, 'chains', array());
                $networks = array();
                $deposit = false;
                $withdraw = false;
                $minWithdrawString = null;
                $minDepositString = null;
                $minWithdrawFeeString = null;
                for ($j = 0; $j < count($chains); $j++) {
                    $chain = $chains[$j];
                    $networkId = $this->safe_string($chain, 'chain');
                    $network = $this->safe_currency_code($networkId);
                    $withdrawEnabled = $this->safe_string($chain, 'withdrawable');
                    $canWithdraw = $withdrawEnabled === 'true';
                    $withdraw = ($canWithdraw) ? $canWithdraw : $withdraw;
                    $depositEnabled = $this->safe_string($chain, 'rechargeable');
                    $canDeposit = $depositEnabled === 'true';
                    $deposit = ($canDeposit) ? $canDeposit : $deposit;
                    $networkWithdrawFeeString = $this->safe_string($chain, 'withdrawFee');
                    if ($networkWithdrawFeeString !== null) {
                        $minWithdrawFeeString = ($minWithdrawFeeString === null) ? $networkWithdrawFeeString : Precise::string_min($networkWithdrawFeeString, $minWithdrawFeeString);
                    }
                    $networkMinWithdrawString = $this->safe_string($chain, 'minWithdrawAmount');
                    if ($networkMinWithdrawString !== null) {
                        $minWithdrawString = ($minWithdrawString === null) ? $networkMinWithdrawString : Precise::string_min($networkMinWithdrawString, $minWithdrawString);
                    }
                    $networkMinDepositString = $this->safe_string($chain, 'minDepositAmount');
                    if ($networkMinDepositString !== null) {
                        $minDepositString = ($minDepositString === null) ? $networkMinDepositString : Precise::string_min($networkMinDepositString, $minDepositString);
                    }
                    $networks[$network] = array(
                        'info' => $chain,
                        'id' => $networkId,
                        'network' => $network,
                        'limits' => array(
                            'withdraw' => array(
                                'min' => $this->parse_number($networkMinWithdrawString),
                                'max' => null,
                            ),
                            'deposit' => array(
                                'min' => $this->parse_number($networkMinDepositString),
                                'max' => null,
                            ),
                        ),
                        'active' => $canWithdraw && $canDeposit,
                        'withdraw' => $canWithdraw,
                        'deposit' => $canDeposit,
                        'fee' => $this->parse_number($networkWithdrawFeeString),
                        'precision' => null,
                    );
                }
                $result[$code] = array(
                    'info' => $entry,
                    'id' => $id,
                    'code' => $code,
                    'networks' => $networks,
                    'type' => null,
                    'name' => null,
                    'active' => $deposit && $withdraw,
                    'deposit' => $deposit,
                    'withdraw' => $withdraw,
                    'fee' => $this->parse_number($minWithdrawFeeString),
                    'precision' => null,
                    'limits' => array(
                        'amount' => array(
                            'min' => null,
                            'max' => null,
                        ),
                        'withdraw' => array(
                            'min' => $this->parse_number($minWithdrawString),
                            'max' => null,
                        ),
                        'deposit' => array(
                            'min' => $this->parse_number($minDepositString),
                            'max' => null,
                        ),
                    ),
                    'created' => null,
                );
            }
            return $result;
        }) ();
    }

    public function fetch_market_leverage_tiers(string $symbol, $params = array ()) {
        return Async\async(function () use ($symbol, $params) {
            /**
             * retrieve information on the maximum leverage, and maintenance margin for trades of varying trade sizes for a single $market
             * @see https://bitgetlimited.github.io/apidoc/en/mix/#get-position-tier
             * @see https://bitgetlimited.github.io/apidoc/en/margin/#get-isolated-tier-data
             * @see https://bitgetlimited.github.io/apidoc/en/margin/#get-cross-tier-data
             * @param {string} $symbol unified $market $symbol
             * @param {array} [$params] extra parameters specific to the bitget api endpoint
             * @param {string} [$params->marginMode] for spot margin 'cross' or 'isolated', default is 'isolated'
             * @param {string} [$params->code] required for cross spot margin
             * @return {array} a {@link https://github.com/ccxt/ccxt/wiki/Manual#leverage-tiers-structure leverage tiers structure}
             */
            Async\await($this->load_markets());
            $request = array();
            $market = $this->market($symbol);
            $type = null;
            list($type, $params) = $this->handle_market_type_and_params('fetchMarketLeverageTiers', $market, $params);
            $response = null;
            $marginMode = null;
            list($marginMode, $params) = $this->handle_margin_mode_and_params('fetchMarketLeverageTiers', $params, 'isolated');
            if (($type === 'swap') || ($type === 'future')) {
                $marketId = $market['id'];
                $parts = explode('_', $marketId);
                $productType = $this->safe_string_upper($parts, 1);
                $request['symbol'] = $marketId;
                $request['productType'] = $productType;
                $response = Async\await($this->publicMixGetMarketQueryPositionLever (array_merge($request, $params)));
            } elseif ($marginMode === 'isolated') {
                $request['symbol'] = $market['info']['symbolName'];
                $response = Async\await($this->publicMarginGetIsolatedPublicTierData (array_merge($request, $params)));
            } elseif ($marginMode === 'cross') {
                $code = $this->safe_string($params, 'code');
                $this->check_required_argument('fetchMarketLeverageTiers', $code, 'code');
                $params = $this->omit($params, 'code');
                $currency = $this->currency($code);
                $request['coin'] = $currency['code'];
                $response = Async\await($this->publicMarginGetCrossPublicTierData (array_merge($request, $params)));
            } else {
                throw new BadRequest($this->id . ' fetchMarketLeverageTiers() $symbol does not support $market ' . $symbol);
            }
            //
            // swap and future
            //
            //     {
            //         "code":"00000",
            //         "data":array(
            //             {
            //                 "level" => 1,
            //                 "startUnit" => 0,
            //                 "endUnit" => 150000,
            //                 "leverage" => 125,
            //                 "keepMarginRate" => "0.004"
            //             }
            //         ),
            //         "msg":"success",
            //         "requestTime":1627292076687
            //     }
            //
            // isolated
            //
            //     {
            //         "code" => "00000",
            //         "msg" => "success",
            //         "requestTime" => 1698352496622,
            //         "data" => array(
            //             array(
            //                 "tier" => "1",
            //                 "symbol" => "BTCUSDT",
            //                 "leverage" => "10",
            //                 "baseCoin" => "BTC",
            //                 "quoteCoin" => "USDT",
            //                 "baseMaxBorrowableAmount" => "3",
            //                 "quoteMaxBorrowableAmount" => "30000",
            //                 "maintainMarginRate" => "0.05",
            //                 "initRate" => "0.1111"
            //             ),
            //         )
            //     }
            //
            // cross
            //
            //     {
            //         "code" => "00000",
            //         "msg" => "success",
            //         "requestTime" => 1698352997077,
            //         "data" => array(
            //             {
            //                 "tier" => "1",
            //                 "leverage" => "3",
            //                 "coin" => "BTC",
            //                 "maxBorrowableAmount" => "26",
            //                 "maintainMarginRate" => "0.1"
            //             }
            //         )
            //     }
            //
            $result = $this->safe_value($response, 'data', array());
            return $this->parse_market_leverage_tiers($result, $market);
        }) ();
    }

    public function parse_market_leverage_tiers($info, $market = null) {
        //
        // swap and future
        //
        //     array(
        //         {
        //             "level" => 1,
        //             "startUnit" => 0,
        //             "endUnit" => 150000,
        //             "leverage" => 125,
        //             "keepMarginRate" => "0.004"
        //         }
        //     )
        //
        // isolated
        //
        //     array(
        //         {
        //             "tier" => "1",
        //             "symbol" => "BTCUSDT",
        //             "leverage" => "10",
        //             "baseCoin" => "BTC",
        //             "quoteCoin" => "USDT",
        //             "baseMaxBorrowableAmount" => "3",
        //             "quoteMaxBorrowableAmount" => "30000",
        //             "maintainMarginRate" => "0.05",
        //             "initRate" => "0.1111"
        //         }
        //     )
        //
        // cross
        //
        //     array(
        //         {
        //             "tier" => "1",
        //             "leverage" => "3",
        //             "coin" => "BTC",
        //             "maxBorrowableAmount" => "26",
        //             "maintainMarginRate" => "0.1"
        //         }
        //     )
        //
        $tiers = array();
        $minNotional = 0;
        for ($i = 0; $i < count($info); $i++) {
            $item = $info[$i];
            $minimumNotional = $this->safe_number($item, 'startUnit');
            if ($minimumNotional !== null) {
                $minNotional = $minimumNotional;
            }
            $maxNotional = $this->safe_number_n($item, array( 'endUnit', 'maxBorrowableAmount', 'baseMaxBorrowableAmount' ));
            $marginCurrency = $this->safe_string_2($item, 'coin', 'baseCoin');
            $currencyId = ($marginCurrency !== null) ? $marginCurrency : $market['base'];
            $tiers[] = array(
                'tier' => $this->safe_integer_2($item, 'level', 'tier'),
                'currency' => $this->safe_currency_code($currencyId),
                'minNotional' => $minNotional,
                'maxNotional' => $maxNotional,
                'maintenanceMarginRate' => $this->safe_number_2($item, 'keepMarginRate', 'maintainMarginRate'),
                'maxLeverage' => $this->safe_number($item, 'leverage'),
                'info' => $item,
            );
            $minNotional = $maxNotional;
        }
        return $tiers;
    }

    public function fetch_deposits(?string $code = null, ?int $since = null, ?int $limit = null, $params = array ()) {
        return Async\async(function () use ($code, $since, $limit, $params) {
            /**
             * fetch all deposits made to an account
             * @see https://bitgetlimited.github.io/apidoc/en/spot/#get-deposit-list
             * @param {string} $code unified $currency $code
             * @param {int} [$since] the earliest time in ms to fetch deposits for
             * @param {int} [$limit] the maximum number of deposits structures to retrieve
             * @param {array} [$params] extra parameters specific to the bitget api endpoint
             * @param {string} [$params->pageNo] pageNo default 1
             * @param {string} [$params->pageSize] pageSize default 20. Max 100
             * @param {int} [$params->until] end tim in ms
             * @param {boolean} [$params->paginate] default false, when true will automatically $paginate by calling this endpoint multiple times. See in the docs all the [availble parameters](https://github.com/ccxt/ccxt/wiki/Manual#pagination-$params)
             * @return {array[]} a list of {@link https://github.com/ccxt/ccxt/wiki/Manual#transaction-structure transaction structures}
             */
            Async\await($this->load_markets());
            $paginate = false;
            list($paginate, $params) = $this->handle_option_and_params($params, 'fetchDeposits', 'paginate');
            if ($paginate) {
                return Async\await($this->fetch_paginated_call_dynamic('fetchDeposits', $code, $since, $limit, $params));
            }
            if ($code === null) {
                throw new ArgumentsRequired($this->id . ' fetchDeposits() requires a `$code` argument');
            }
            $currency = $this->currency($code);
            if ($since === null) {
                $since = $this->milliseconds() - 7776000000; // 90 days
            }
            $request = array(
                'coin' => $currency['code'],
                'startTime' => $since,
                'endTime' => $this->milliseconds(),
            );
