    public function withdraw(string $code, $amount, $address, $tag = null, $params = array ()) {
        return Async\async(function () use ($code, $amount, $address, $tag, $params) {
            /**
             * make a withdrawal
             * @see https://bitgetlimited.github.io/apidoc/en/spot/#withdraw-v2
             * @param {string} $code unified $currency $code
             * @param {float} $amount the $amount to withdraw
             * @param {string} $address the $address to withdraw to
             * @param {string} $tag
             * @param {array} [$params] extra parameters specific to the bitget api endpoint
             * @param {string} [$params->chain] the $chain to withdraw to
             * @return {array} a {@link https://github.com/ccxt/ccxt/wiki/Manual#transaction-structure transaction structure}
             */
            $this->check_address($address);
            $chain = $this->safe_string_2($params, 'chain', 'network');
            $params = $this->omit($params, array( 'network' ));
            if ($chain === null) {
                throw new ArgumentsRequired($this->id . ' withdraw() requires a $chain parameter or a network parameter');
            }
            Async\await($this->load_markets());
            $currency = $this->currency($code);
            $networkId = $this->network_code_to_id($chain);
            $request = array(
                'coin' => $currency['code'],
                'address' => $address,
                'chain' => $networkId,
                'amount' => $amount,
            );
            if ($tag !== null) {
                $request['tag'] = $tag;
            }
            $response = Async\await($this->privateSpotPostWalletWithdrawalV2 (array_merge($request, $params)));
            //
            //     {
            //         "code" => "00000",
            //         "msg" => "success",
            //         "data" => "888291686266343424"
            //     }
            //
            //     {
            //          "code":"00000",
            //          "msg":"success",
            //          "requestTime":1696784219602,
            //          "data":{
            //              "orderId":"1094957867615789056",
            //              "clientOrderId":"64f1e4ce842041d296b4517df1b5c2d7"
            //          }
            //      }
            //
            $data = $this->safe_value($response, 'data');
            $id = null;
            if (gettype($data) === 'string') {
                $id = $data;
            } elseif ($data !== null) {
                $id = $this->safe_string($data, 'orderId');
            }
            $result = array(
                'id' => $id,
                'info' => $response,
                'txid' => null,
                'timestamp' => null,
                'datetime' => null,
                'network' => null,
                'addressFrom' => null,
                'address' => null,
                'addressTo' => null,
                'amount' => null,
                'type' => 'withdrawal',
                'currency' => null,
                'status' => null,
                'updated' => null,
                'tagFrom' => null,
                'tag' => null,
                'tagTo' => null,
                'comment' => null,
                'fee' => null,
            );
            $withdrawOptions = $this->safe_value($this->options, 'withdraw', array());
            $fillResponseFromRequest = $this->safe_value($withdrawOptions, 'fillResponseFromRequest', true);
            if ($fillResponseFromRequest) {
                $result['currency'] = $code;
                $result['timestamp'] = $this->milliseconds();
                $result['datetime'] = $this->iso8601($this->milliseconds());
                $result['amount'] = $amount;
                $result['tag'] = $tag;
                $result['address'] = $address;
                $result['addressTo'] = $address;
                $result['network'] = $chain;
            }
            return $result;
        }) ();
    }

    public function fetch_withdrawals(?string $code = null, ?int $since = null, ?int $limit = null, $params = array ()) {
        return Async\async(function () use ($code, $since, $limit, $params) {
            /**
             * fetch all withdrawals made from an account
             * @see https://bitgetlimited.github.io/apidoc/en/spot/#get-withdraw-list
             * @param {string} $code unified $currency $code
             * @param {int} [$since] the earliest time in ms to fetch withdrawals for
             * @param {int} [$limit] the maximum number of withdrawals structures to retrieve
             * @param {array} [$params] extra parameters specific to the bitget api endpoint
             * @param {string} [$params->pageNo] pageNo default 1
             * @param {string} [$params->pageSize] pageSize default 20. Max 100
             * @param {int} [$params->until] end time in ms
             * @param {boolean} [$params->paginate] default false, when true will automatically $paginate by calling this endpoint multiple times. See in the docs all the [availble parameters](https://github.com/ccxt/ccxt/wiki/Manual#pagination-$params)
             * @return {array[]} a list of {@link https://github.com/ccxt/ccxt/wiki/Manual#transaction-structure transaction structures}
             */
            Async\await($this->load_markets());
            $paginate = false;
            list($paginate, $params) = $this->handle_option_and_params($params, 'fetchWithdrawals', 'paginate');
            if ($paginate) {
                return Async\await($this->fetch_paginated_call_dynamic('fetchWithdrawals', $code, $since, $limit, $params));
            }
            if ($code === null) {
                throw new ArgumentsRequired($this->id . ' fetchWithdrawals() requires a `$code` argument');
            }
            $currency = $this->currency($code);
            if ($since === null) {
                $since = $this->milliseconds() - 7776000000; // 90 days
            }
            $request = array(
                'coin' => $currency['code'],
                'startTime' => $since,
                'endTime' => $this->milliseconds(),
            );
            list($request, $params) = $this->handle_until_option('endTime', $request, $params);
            if ($limit !== null) {
                $request['pageSize'] = $limit;
            }
            $response = Async\await($this->privateSpotGetWalletWithdrawalList (array_merge($request, $params)));
