            //                 "interest" => "0",
            //                 "net" => "0.00021654",
            //                 "ctime" => "1697248128071"
            //             ),
            //         )
            //     }
            //
            // cross margin
            //
            //     {
            //         "code" => "00000",
            //         "msg" => "success",
