            if ($dataLength > 0) {
                $first = $data[0];
                $last = $data[$dataLength - 1];
                $first['endId'] = $endId;
                $last['endId'] = $endId;
                $data[0] = $first;
                $data[$dataLength - 1] = $last;
            }
        }
        return $data;
    }

