    public function parse_margin_modification($data, $market = null) {
        $errorCode = $this->safe_string($data, 'code');
        $status = ($errorCode === '00000') ? 'ok' : 'failed';
        $code = ($market['linear']) ? $market['quote'] : $market['base'];
        return array(
            'info' => $data,
            'type' => null,
            'amount' => null,
            'code' => $code,
            'symbol' => $market['symbol'],
            'status' => $status,
        );
    }

