        //         "rechargeable" => "true",
        //         "withdrawConfirm" => "15",
        //         "withdrawFee" => "0.0000051",
        //         "withdrawable" => "true"
        //       ),
        //       {
        //         "browserUrl" => "https://blockchair.com/bitcoin/transaction/",
        //         "chain" => "BTC",
        //         "depositConfirm" => "1",
        //         "extraWithDrawFee" => "0",
        //         "minDepositAmount" => "0.0001",
        //         "minWithdrawAmount" => "0.002",
        //         "needTag" => "false",
        //         "rechargeable" => "true",
        //         "withdrawConfirm" => "1",
        //         "withdrawFee" => "0.0005",
        //         "withdrawable" => "true"
        //       }
        //     ),
        //     "coinId" => "1",
        //     "coinName" => "BTC",
        //     "transfer" => "true"
        // }
        //
        $chains = $this->safe_value($fee, 'chains', array());
        $chainsLength = count($chains);
        $result = array(
            'info' => $fee,
            'withdraw' => array(
                'fee' => null,
                'percentage' => null,
            ),
            'deposit' => array(
                'fee' => null,
                'percentage' => null,
            ),
            'networks' => array(),
        );
        for ($i = 0; $i < $chainsLength; $i++) {
            $chain = $chains[$i];
            $networkId = $this->safe_string($chain, 'chain');
            $currencyCode = $this->safe_string($currency, 'code');
            $networkCode = $this->network_id_to_code($networkId, $currencyCode);
            $result['networks'][$networkCode] = array(
                'deposit' => array( 'fee' => null, 'percentage' => null ),
                'withdraw' => array( 'fee' => $this->safe_number($chain, 'withdrawFee'), 'percentage' => false ),
            );
            if ($chainsLength === 1) {
                $result['withdraw']['fee'] = $this->safe_number($chain, 'withdrawFee');
                $result['withdraw']['percentage'] = false;
            }
        }
        return $result;
    }

