            $data = $this->safe_value($response, 'data', array());
            $rows = $this->safe_value($data, 'resultList', array());
            $interest = $this->parse_borrow_interests($rows, $market);
            return $this->filter_by_currency_since_limit($interest, $code, $since, $limit);
        }) ();
