    public function fetch_ledger(?string $code = null, ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($code, $since, $limit, $params) {
            /**
             * fetch the history of changes, actions done by the user or operations that altered the balance of the user
             *
             * @see https://www.bitget.com/api-doc/spot/account/Get-Account-Bills
             * @see https://www.bitget.com/api-doc/contract/account/Get-Account-Bill
             *
             * @param {string} [$code] unified $currency $code, default is null
             * @param {int} [$since] timestamp in ms of the earliest ledger entry, default is null
             * @param {int} [$limit] max number of ledger entries to return, default is null
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @param {int} [$params->until] end time in ms
             * @param {string} [$params->symbol] *contract only* unified $market $symbol
             * @param {string} [$params->productType] *contract only* 'USDT-FUTURES', 'USDC-FUTURES', 'COIN-FUTURES', 'SUSDT-FUTURES', 'SUSDC-FUTURES' or 'SCOIN-FUTURES'
             * @param {boolean} [$params->paginate] default false, when true will automatically $paginate by calling this endpoint multiple times. See in the docs all the [available parameters](https://github.com/ccxt/ccxt/wiki/Manual#pagination-$params)
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=ledger ledger structure~
             */
            Async\await($this->load_markets());
            $symbol = $this->safe_string($params, 'symbol');
            $params = $this->omit($params, 'symbol');
            $market = null;
            if ($symbol !== null) {
                $sandboxMode = $this->safe_bool($this->options, 'sandboxMode', false);
                if ($sandboxMode) {
                    $sandboxSymbol = $this->convert_symbol_for_sandbox($symbol);
                    $market = $this->market($sandboxSymbol);
                } else {
                    $market = $this->market($symbol);
                }
            }
            $marketType = null;
            list($marketType, $params) = $this->handle_market_type_and_params('fetchLedger', $market, $params);
            $paginate = false;
            list($paginate, $params) = $this->handle_option_and_params($params, 'fetchLedger', 'paginate');
            if ($paginate) {
                $cursorReceived = null;
                if ($marketType !== 'spot') {
                    $cursorReceived = 'endId';
                }
                return Async\await($this->fetch_paginated_call_cursor('fetchLedger', $symbol, $since, $limit, $params, $cursorReceived, 'idLessThan'));
            }
            $currency = null;
            $request = array();
            if ($code !== null) {
                $currency = $this->currency($code);
                $request['coin'] = $currency['id'];
            }
            list($request, $params) = $this->handle_until_option('endTime', $request, $params);
            if ($since !== null) {
                $request['startTime'] = $since;
            }
            if ($limit !== null) {
                $request['limit'] = $limit;
            }
            $response = null;
            if ($marketType === 'spot') {
                $response = Async\await($this->privateSpotGetV2SpotAccountBills ($this->extend($request, $params)));
            } else {
                if ($symbol !== null) {
                    $request['symbol'] = $market['id'];
                }
                $productType = null;
                list($productType, $params) = $this->handle_product_type_and_params($market, $params);
                $request['productType'] = $productType;
                $response = Async\await($this->privateMixGetV2MixAccountBill ($this->extend($request, $params)));
            }
            //
            // spot
            //
            //     {
            //         "code" => "00000",
            //         "msg" => "success",
            //         "requestTime" => 1700795836415,
            //         "data" => array(
            //             array(
            //                 "billId" => "1111506298997215233",
            //                 "coin" => "USDT",
            //                 "groupType" => "transfer",
            //                 "businessType" => "transfer_out",
            //                 "size" => "-11.64958799",
            //                 "balance" => "0.00000000",
            //                 "fees" => "0.00000000",
            //                 "cTime" => "1700729673028"
            //             ),
            //         )
            //     }
            //
            // swap and future
            //
            //     {
            //         "code" => "00000",
            //         "msg" => "success",
            //         "requestTime" => 1700795977890,
            //         "data" => {
            //             "bills" => array(
            //                 array(
            //                     "billId" => "1111499428100472833",
            //                     "symbol" => "",
            //                     "amount" => "-11.64958799",
            //                     "fee" => "0",
            //                     "feeByCoupon" => "",
            //                     "businessType" => "trans_to_exchange",
            //                     "coin" => "USDT",
            //                     "cTime" => "1700728034996"
            //                 ),
            //             ),
            //             "endId" => "1098396773329305606"
            //         }
            //     }
            //
            $data = $this->safe_value($response, 'data');
            if (($marketType === 'swap') || ($marketType === 'future')) {
                $bills = $this->safe_value($data, 'bills', array());
                return $this->parse_ledger($bills, $currency, $since, $limit);
            }
            return $this->parse_ledger($data, $currency, $since, $limit);
        }) ();
    }

    public function parse_ledger_entry(array $item, ?array $currency = null): array {
        //
        // spot
        //
        //     {
        //         "billId" => "1111506298997215233",
        //         "coin" => "USDT",
        //         "groupType" => "transfer",
        //         "businessType" => "transfer_out",
        //         "size" => "-11.64958799",
        //         "balance" => "0.00000000",
        //         "fees" => "0.00000000",
        //         "cTime" => "1700729673028"
        //     }
        //
        // swap and future
        //
        //     {
        //         "billId" => "1111499428100472833",
        //         "symbol" => "",
        //         "amount" => "-11.64958799",
        //         "fee" => "0",
        //         "feeByCoupon" => "",
        //         "businessType" => "trans_to_exchange",
        //         "coin" => "USDT",
        //         "cTime" => "1700728034996"
        //     }
        //
        $currencyId = $this->safe_string($item, 'coin');
        $code = $this->safe_currency_code($currencyId, $currency);
        $currency = $this->safe_currency($currencyId, $currency);
        $timestamp = $this->safe_integer($item, 'cTime');
        $after = $this->safe_number($item, 'balance');
        $fee = $this->safe_number_2($item, 'fees', 'fee');
        $amountRaw = $this->safe_string_2($item, 'size', 'amount');
        $amount = $this->parse_number(Precise::string_abs($amountRaw));
        $direction = 'in';
        if (mb_strpos($amountRaw, '-') !== false) {
            $direction = 'out';
        }
        return $this->safe_ledger_entry(array(
            'info' => $item,
            'id' => $this->safe_string($item, 'billId'),
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'direction' => $direction,
            'account' => null,
            'referenceId' => null,
            'referenceAccount' => null,
            'type' => $this->parse_ledger_type($this->safe_string($item, 'businessType')),
            'currency' => $code,
            'amount' => $amount,
            'before' => null,
            'after' => $after,
            'status' => null,
            'fee' => array(
                'currency' => $code,
                'cost' => $fee,
            ),
        ), $currency);
    }

