    public function fetch_funding_history(?string $symbol = null, ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * fetch the funding history
             *
             * @see https://www.bitget.com/api-doc/contract/account/Get-Account-Bill
             *
             * @param {string} $symbol unified $market $symbol
             * @param {int} [$since] the starting timestamp in milliseconds
             * @param {int} [$limit] the number of entries to return
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @param {int} [$params->until] the latest time in ms to fetch funding history for
             * @param {boolean} [$params->paginate] default false, when true will automatically $paginate by calling this endpoint multiple times. See in the docs all the [available parameters](https://github.com/ccxt/ccxt/wiki/Manual#pagination-$params)
             * @return {array[]} a list of ~@link https://docs.ccxt.com/#/?id=funding-history-structure funding history structures~
             */
            Async\await($this->load_markets());
            if ($symbol === null) {
                throw new ArgumentsRequired($this->id . ' fetchFundingHistory() requires a $symbol argument');
            }
            $paginate = false;
            list($paginate, $params) = $this->handle_option_and_params($params, 'fetchFundingHistory', 'paginate');
            if ($paginate) {
                return Async\await($this->fetch_paginated_call_cursor('fetchFundingHistory', $symbol, $since, $limit, $params, 'endId', 'idLessThan'));
            }
            $sandboxMode = $this->safe_bool($this->options, 'sandboxMode', false);
            $market = null;
            if ($sandboxMode) {
                $sandboxSymbol = $this->convert_symbol_for_sandbox($symbol);
                $market = $this->market($sandboxSymbol);
            } else {
                $market = $this->market($symbol);
            }
            if (!$market['swap']) {
                throw new BadSymbol($this->id . ' fetchFundingHistory() supports swap contracts only');
            }
            $productType = null;
            list($productType, $params) = $this->handle_product_type_and_params($market, $params);
            $request = array(
                'symbol' => $market['id'],
                'marginCoin' => $market['settleId'],
                'businessType' => 'contract_settle_fee',
                'productType' => $productType,
            );
            list($request, $params) = $this->handle_until_option('endTime', $request, $params);
            if ($since !== null) {
                $request['startTime'] = $since;
            }
            if ($limit !== null) {
                $request['limit'] = $limit;
            }
            $response = Async\await($this->privateMixGetV2MixAccountBill ($this->extend($request, $params)));
            //
            //     {
            //         "code" => "00000",
            //         "msg" => "success",
            //         "requestTime" => 1700795977890,
            //         "data" => {
            //             "bills" => array(
            //                 array(
            //                     "billId" => "1111499428100472833",
            //                     "symbol" => "BTCUSDT",
            //                     "amount" => "-0.004992",
            //                     "fee" => "0",
            //                     "feeByCoupon" => "",
            //                     "businessType" => "contract_settle_fee",
            //                     "coin" => "USDT",
            //                     "cTime" => "1700728034996"
            //                 ),
            //             ),
            //             "endId" => "1098396773329305606"
            //         }
            //     }
            //
            $data = $this->safe_value($response, 'data', array());
            $result = $this->safe_value($data, 'bills', array());
            return $this->parse_funding_histories($result, $market, $since, $limit);
        }) ();
    }

    public function parse_funding_history($contract, ?array $market = null) {
        //
        //     {
        //         "billId" => "1111499428100472833",
        //         "symbol" => "BTCUSDT",
        //         "amount" => "-0.004992",
        //         "fee" => "0",
        //         "feeByCoupon" => "",
        //         "businessType" => "contract_settle_fee",
        //         "coin" => "USDT",
        //         "cTime" => "1700728034996"
        //     }
        //
        $marketId = $this->safe_string($contract, 'symbol');
        $currencyId = $this->safe_string($contract, 'coin');
        $timestamp = $this->safe_integer($contract, 'cTime');
        return array(
            'info' => $contract,
            'symbol' => $this->safe_symbol($marketId, $market, null, 'swap'),
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'code' => $this->safe_currency_code($currencyId),
            'amount' => $this->safe_number($contract, 'amount'),
            'id' => $this->safe_string($contract, 'billId'),
        );
    }

    public function parse_funding_histories($contracts, $market = null, ?int $since = null, ?int $limit = null): array {
        $result = array();
        for ($i = 0; $i < count($contracts); $i++) {
            $contract = $contracts[$i];
            $business = $this->safe_string($contract, 'businessType');
            if ($business !== 'contract_settle_fee') {
                continue;
            }
            $result[] = $this->parse_funding_history($contract, $market);
        }
        $sorted = $this->sort_by($result, 'timestamp');
        return $this->filter_by_since_limit($sorted, $since, $limit);
    }

    public function modify_margin_helper(string $symbol, $amount, $type, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $amount, $type, $params) {
            Async\await($this->load_markets());
            $holdSide = $this->safe_string($params, 'holdSide');
            $sandboxMode = $this->safe_bool($this->options, 'sandboxMode', false);
            $market = null;
            if ($sandboxMode) {
                $sandboxSymbol = $this->convert_symbol_for_sandbox($symbol);
                $market = $this->market($sandboxSymbol);
            } else {
                $market = $this->market($symbol);
            }
            $productType = null;
            list($productType, $params) = $this->handle_product_type_and_params($market, $params);
            $request = array(
                'symbol' => $market['id'],
                'marginCoin' => $market['settleId'],
                'amount' => $this->amount_to_precision($symbol, $amount), // positive value for adding margin, negative for reducing
                'holdSide' => $holdSide, // long or short
                'productType' => $productType,
            );
            $params = $this->omit($params, 'holdSide');
            $response = Async\await($this->privateMixPostV2MixAccountSetMargin ($this->extend($request, $params)));
            //
            //     {
            //         "code" => "00000",
            //         "msg" => "success",
            //         "requestTime" => 1700813444618,
            //         "data" => ""
            //     }
            //
            return $this->extend($this->parse_margin_modification($response, $market), array(
                'amount' => $this->parse_number($amount),
                'type' => $type,
            ));
        }) ();
    }

    public function parse_margin_modification(array $data, ?array $market = null): array {
        //
        // addMargin/reduceMargin
        //
        //     {
        //         "code" => "00000",
        //         "msg" => "success",
        //         "requestTime" => 1700813444618,
        //         "data" => ""
        //     }
