            //
            // cross
            //
            //     {
            //         "code" => "00000",
            //         "msg" => "success",
            //         "requestTime" => 1700879597044,
            //         "data" => {
            //             "resultList" => array(
            //                 array(
            //                     "interestId" => "1112122013642272769",
