        //     array("code":"40108","msg":"","requestTime":1595885064600,"data":null)
        //     array("order_id":"513468410013679613","client_oid":null,"symbol":"ethusd","result":false,"err_code":"order_no_exist_error","err_msg":"è®¢å•ä¸å­˜åœ¨ï¼")
        //
        $message = $this->safe_string_2($response, 'err_msg', 'msg');
        $feedback = $this->id . ' ' . $body;
        $nonEmptyMessage = (($message !== null) && ($message !== '') && ($message !== 'success'));
        if ($nonEmptyMessage) {
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $message, $feedback);
            $this->throw_broadly_matched_exception($this->exceptions['broad'], $message, $feedback);
        }
        $errorCode = $this->safe_string_2($response, 'code', 'err_code');
        $nonZeroErrorCode = ($errorCode !== null) && ($errorCode !== '00000');
        if ($nonZeroErrorCode) {
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $errorCode, $feedback);
        }
        if ($nonZeroErrorCode || $nonEmptyMessage) {
            throw new ExchangeError($feedback); // unknown $message
        }
        return null;
    }

    public function nonce() {
        return $this->milliseconds() - $this->options['timeDifference'];
    }

    public function sign($path, $api = [], $method = 'GET', $params = array (), $headers = null, $body = null) {
        $signed = $api[0] === 'private';
        $endpoint = $api[1];
        $pathPart = '/api';
        $request = '/' . $this->implode_params($path, $params);
        $payload = $pathPart . $request;
        $url = $this->implode_hostname($this->urls['api'][$endpoint]) . $payload;
        $query = $this->omit($params, $this->extract_params($path));
        if (!$signed && ($method === 'GET')) {
            $keys = is_array($query) ? array_keys($query) : array();
            $keysLength = count($keys);
            if ($keysLength > 0) {
                $url = $url . '?' . $this->urlencode($query);
            }
        }
        if ($signed) {
            $this->check_required_credentials();
            $timestamp = (string) $this->nonce();
            $auth = $timestamp . $method . $payload;
            if ($method === 'POST') {
                $body = $this->json($params);
                $auth .= $body;
            } else {
                if ($params) {
                    $queryInner = '?' . $this->urlencode($this->keysort($params));
                    // check #21169 pr
                    if (mb_strpos($queryInner, '%24') > -1) {
                        $queryInner = str_replace('%24', '$', $queryInner);
                    }
                    $url .= $queryInner;
                    $auth .= $queryInner;
                }
            }
            $signature = $this->hmac($this->encode($auth), $this->encode($this->secret), 'sha256', 'base64');
            $broker = $this->safe_string($this->options, 'broker');
            $headers = array(
                'ACCESS-KEY' => $this->apiKey,
                'ACCESS-SIGN' => $signature,
                'ACCESS-TIMESTAMP' => $timestamp,
                'ACCESS-PASSPHRASE' => $this->password,
                'X-CHANNEL-API-CODE' => $broker,
            );
            if ($method === 'POST') {
                $headers['Content-Type'] = 'application/json';
            }
        }
        $sandboxMode = $this->safe_bool($this->options, 'sandboxMode', false);
        if ($sandboxMode && ($path !== 'v2/public/time')) {
            // https://github.com/ccxt/ccxt/issues/25252#issuecomment-2662742336
            if ($headers === null) {
                $headers = array();
            }
            $headers['PAPTRADING'] = '1';
        }
        return array( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }
}
